import React from 'react';

import { Router, Route, Switch, Redirect } from 'react-router-dom';

import history from './history';

import LoginScreen from './screens/Auth/Login';
import RegisterScreen from './screens/Auth/Register';
import TwoFA from './screens/Auth/TwoFA';
import ForgotPassword from './screens/Auth/ForgotPassword';
import ResetPassword from './screens/Auth/ResetPassword';

import ProfileScreen from './screens/User/Profile';

import SalesHomeScreen from './screens/Sales/Home';
import ContactScreen from './screens/Sales/Contact';
import ReorderScreen from './screens/Sales/Reorder';
import ProductScreen from './screens/Sales/Product';
import SummaryScreen from './screens/Sales/Summary';
import PrequalifyScreen from './screens/Sales/Prequalify';

import DealerHomeScreen from './screens/Dealer/Home';
import SalesListScreen from './screens/Dealer/Sales';
import AppDetailsScreen from './screens/Dealer/AppDetails';
import ApplicationDetailsScreen from './screens/Dealer/ApplicationDetails';
import CustomerDetailsScreen from './screens/Dealer/Customer';
import DealerPreApprovals from './screens/Dealer/DealerPreApprovals';
import DealerIncompletes from './screens/Dealer/DealerIncomplete';
import DealerFix from './screens/Dealer/DealerFix';

import AdminHome from './screens/Admin/Home';
import AdminPreApprovals from './screens/Admin/AdminPreApprovals';
import AdminPreApprovalItem from './screens/Admin/AdminPreApprovalItem';
import AdminFundingRequests from './screens/Admin/FundingRequests';
import AdminFundingRequestItem from './screens/Admin/FundingRequestItem';

import 'bootstrap/dist/css/bootstrap.min.css';
import './assets/css/index.scss';

const PrivateRoute = ({ component: Component, ...rest }) => (
  <Route {...rest} render={(props) => {
    const token = localStorage.getItem('token');
    const role = ['admin']
    //localStorage.getItem('role');

    if (token) 
    {
      if(role === "admin" && rest.path === "/")
      {
        return <AdminHome {...props} />;
      }
      else if(role === "dealer" && rest.path === "/")
      {
        return <DealerHomeScreen {...props} />;
      }
      else if(role === "sales" && rest.path === "/")
      {
        return <SalesHomeScreen {...props} />;
      }
      else
      {
        return <Component {...props} />;
      }
    } 
    else
    {
      return <Redirect to='/login' />;
    }
  }} />
)

const AuthRoute = ({ component: Component, ...rest }) => (
  <Route {...rest} render={(props) => {
    const token = localStorage.getItem('token');
    if (!token) {
      return <Component {...props} />;
    } else {
      return <Redirect to='/' />;
    }
  }} />
)

function App() {
  return (
    <Router history={history}>
      <Switch> 

        <AuthRoute exact path="/login" component={LoginScreen} />
        <AuthRoute exact path="/register" component={RegisterScreen} />
        <AuthRoute exact path="/2fa" component={TwoFA} />
        <AuthRoute exact path="/forgot" component={ForgotPassword} />
        <PrivateRoute exact path="/reset-password" component={ResetPassword} />

        <PrivateRoute exact path="/" component={DealerHomeScreen} />
        <PrivateRoute exact path="/sales-list/:status" component={SalesListScreen} />
        <PrivateRoute exact path="/preapprovals" component={DealerPreApprovals} />
        <PrivateRoute exact path="/incompletes" component={DealerIncompletes} />
        <PrivateRoute exact path="/dealer-fix/:appId" component={DealerFix} />
        <PrivateRoute exact path="/app-details/:appId" component={AppDetailsScreen} />
        <PrivateRoute exact path="/application-details/:appId" component={ApplicationDetailsScreen} />
        <PrivateRoute exact path="/customer/:id" component={CustomerDetailsScreen} />

        <PrivateRoute exact path="/" component={SalesHomeScreen} />
        <PrivateRoute exact path="/profile" component={ProfileScreen} />
        <PrivateRoute exact path="/contact" component={ContactScreen} />
        <PrivateRoute exact path="/reorder" component={ReorderScreen} />
        <PrivateRoute exact path="/product/:id" component={ProductScreen} key=":id" />
        <PrivateRoute exact path="/summary" component={SummaryScreen} />
        <PrivateRoute exact path="/prequalify" component={PrequalifyScreen} />

        <PrivateRoute exact path="/" component={AdminHome} />
        <PrivateRoute exact path="/admin/preapprovals" component={AdminPreApprovals} />
        <PrivateRoute exact path="/admin/preapproval/:id" component={AdminPreApprovalItem} key=":id" />
        <PrivateRoute exact path="/admin/fundings" component={AdminFundingRequests} />
        <PrivateRoute exact path="/admin/funding/:id" component={AdminFundingRequestItem} key=":id" />
      </Switch>
    </Router>
  );
}

export default App;
