import React, { useState } from 'react';
import { connect } from 'react-redux';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { getMe } from '../../redux/actions/auth';
import { getUserDetails } from '../../redux/actions/userAction';
import { clearCustomer, changeCustomer } from '../../redux/actions/customer';
import { Form } from 'react-bootstrap';
import Input from '../../components/commons/input';
import Loader from 'shared/Loader';
import { TCPLogo } from '../../assets/images';

function HomeScreen(props) {

    const {
        history,
        actionLoading,
    } = props;

    const [validationResult, setValidationResult] = useState(null);

    return (
        <div className="dealer">

            { actionLoading && <Loader />}

            <Header isHome={true} history={history} avatar={avatar}>
                {localStorage.getItem('role') && localStorage.getItem('role').indexOf('dealer') !== -1 &&
                    <img src={IconHome} alt="home" className="icon-home" onClick={this.handleHomeClick} />
                }
            </Header>

            <div className='main-box'>
                <p className="title">ENTER CUSTOMER INFORMATION</p>
                <p className="sub-title">Short instruction donec in semper arcu. Sed quis ante cursus, porta lectus sit amet, mollis lacus.</p>
            </div>


            <form noValidate>
                <div className="container">
                    <div className="styled-form">
                        <div className="box color-box">
                            <Form.Group className="mb-18">
                                <Input
                                    name="email"
                                    type="email"
                                    regex="^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    label="Main Dealer User Email"
                                    defaultText="Main Dealer User Email"
                                    required={true}
                                    error={{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Main Dealer User Email"
                                    }}
                                    validationResult={validationResult}
                                />
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Input
                                    name="contact_email"
                                    type="email"
                                    regex="^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    label="Dealer Contact Email"
                                    defaultText="Dealer Contact Email"
                                    required={true}
                                    error={{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Dealer Contact Email"
                                    }}
                                    validationResult={validationResult}
                                />
                            </Form.Group>
                        </div>
                        <Form.Group className="mb-18">
                            <Input
                                name="city"
                                type="text"
                                label="City"
                                defaultText="City"
                                required={true}
                                error={{
                                    'empty': "Please enter city"
                                }}
                                validationResult={validationResult}
                            />
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Input
                                name="city"
                                type="text"
                                label="City"
                                defaultText="City"
                                required={true}
                                error={{
                                    'empty': "Please enter city"
                                }}
                                validationResult={validationResult}
                            />
                        </Form.Group>
                    </div>
                </div>
                <div className="footer-container">
                    <button className="secondary" type="submit">Next</button>
                </div>
            </form>
        </div>
    )
}

const mapStateToProps = state => ({
});

const mapDispatchToProps = dispatch => ({
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(HomeScreen);
