import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconAdd, IconArrowLeft, IconDeleteNew, IconPreapprovalWaiting, IconPreapprovalDeclined, IconStatusConfirmed } from '../../../assets/images';

import { getFundingRequests, setSelectedFundingRequest } from '../../../redux/actions/admin';
import Loader from 'shared/Loader';

class FundingRequests extends Component {

    state = {
        seachKeyword: '',
        dealerArray: [{},{},{},{},{},{},{},{},{},{},{},{},{}],
        openDealerIndex: null
    }

    componentDidMount() {
        this.props.getFundingRequests();
    }

    handleArrowBack = () => {       
        const { history } = this.props;
        history.push('/')
    }

    handleAddDealer = () => {       
        const { history } = this.props;
        this.props.history.push(`/admin/addDealer`);
    }

    handleRowClick = (item) => {
        this.props.setSelectedFundingRequest(item);
        this.props.history.push(`/admin/funding/${item.id}`);
    }

    handleOpenDealerAction = (mapId, acctrefno) => {
        if(mapId === this.state.openDealerIndex) {
            this.setState({
                openDealerIndex: null,
            });
        } else {
            this.setState({
                openDealerIndex: mapId,
            });
        }
    };

    render() {

        const { seachKeyword, dealerArray, openDealerIndex } = this.state;
        const { data } = this.props;

        const IconSmallArrowRight = (props) => {
            return (
                <svg className={`arrow-icon ${props.id === openDealerIndex && "arrow-icon-active"}`} enableBackground="new 0 0 12 12" height="12px" id="Layer_1" version="1.1" viewBox="0 0 32 32" width="32px">
                    <path d="M24.291,14.276L14.705,4.69c-0.878-0.878-2.317-0.878-3.195,0l-0.8,0.8c-0.878,0.877-0.878,2.316,0,3.194  L18.024,16l-7.315,7.315c-0.878,0.878-0.878,2.317,0,3.194l0.8,0.8c0.878,0.879,2.317,0.879,3.195,0l9.586-9.587  c0.472-0.471,0.682-1.103,0.647-1.723C24.973,15.38,24.763,14.748,24.291,14.276z"/>
                </svg>
            )
        }

        return (
            <div className="dealer">
                {
                    data.loading && <Loader />
                }
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} alt=""/>
                    </HeaderLeft>
                    <HeaderCenter>
                        <div className="header-main">
                            <img className="main-logo" src={TCPLogo} alt=""/>
                        </div>
                    </HeaderCenter>
                    <HeaderRight></HeaderRight>
                </Header>
                <div className="main">                    
                    <div className="form">
                        <Form onSubmit={this.handleSearchClick}>     
                            <div id="form">
                                <Form.Group className="mb-18">
                                    <Form.Control value={seachKeyword} placeholder="Search" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                                </Form.Group>
                            </div>
                            <button type="submit" style={{"display": "none"}} />
                        </Form>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    data: state.admin.fundings
});

export default connect(
    mapStateToProps,
    {
        getFundingRequests,
        setSelectedFundingRequest
    }
)(FundingRequests);
