import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft } from '../../../assets/images';
import { updateDealer } from '../../../redux/actions/admin';

function AddDealer(props) {

    const {
        history,
        selectedDealer
    } = props;

    const [dealer, setDealer] = useState({
        id: null,
        company_name: null,
        email: null,
        contact_email: null,
        phone: null,
        street: null,
        city: null,
        state: null,
        zip: null,
    });

    // state = {
    //     id: this.props.selectedDealer.id,
    //     company_name: this.props.selectedDealer.company_name,
    //     email: this.props.selectedDealer.email,
    //     contact_email: this.props.selectedDealer.contact_email,
    //     phone: this.props.selectedDealer.phone,
    //     street: this.props.selectedDealer.street,
    //     city: this.props.selectedDealer.city,
    //     state: this.props.selectedDealer.state,
    //     zip: this.props.selectedDealer.zip,
    // }

    const handleArrowBack = () => {       
        history.push('/admin/dealers')
    }

    // handleAddUpdateDealer = () => {    

    //     const data = {
    //         id: this.state.id,
    //         company_name: this.state.company_name,
    //         email: this.state.email,
    //         contact_email: this.state.contact_email,
    //         phone: this.state.phone,
    //         street: this.state.street,
    //         city: this.state.city,
    //         state: this.state.state,
    //         zip: this.state.zip,
    //     }

    //     this.props.updateDealer(data)
    // }

    return (
        <div className="dealer">

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt=""/>
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt=""/>
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <div className="container">                    
                <div className="styled-form">
                    <Form>
                        <Form.Group className="mb-18">
                            <Form.Label>Dealer Name</Form.Label>
                            <Form.Control value={dealer.company_name} placeholder="Dealer Name" onChange={(e) => setDealer({...dealer, company_name: e.target.value })}></Form.Control>
                        </Form.Group>
                        <div className="box">
                            <Form.Group className="mb-18">
                                <Form.Label>Main Dealer User Email</Form.Label>
                                <Form.Control value={email} placeholder="Main Dealer User Email" onChange={(e) => this.setState({ email: e.target.value })}></Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Form.Label>Dealer Contact Email</Form.Label>
                                <Form.Control value={contact_email} placeholder="Dealer Contact Email" onChange={(e) => this.setState({ contact_email: e.target.value })}></Form.Control>
                            </Form.Group>
                        </div>
                        <Form.Group className="mb-18">
                            <Form.Label>Phone</Form.Label>
                            <Form.Control value={phone} placeholder="Phone" onChange={(e) => this.setState({ phone: e.target.value })}></Form.Control>
                        </Form.Group>
                        <span className="divider"></span>
                        <span className="title">ADDRESS</span>
                        <Form.Group className="mb-18">
                            <Form.Label>Street</Form.Label>
                            <Form.Control value={street} placeholder="Street" onChange={(e) => this.setState({ street: e.target.value })}></Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>City</Form.Label>
                            <Form.Control value={city} placeholder="City" onChange={(e) => this.setState({ city: e.target.value })}></Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>State</Form.Label>
                            <select value={state} placeholder="State" onChange={(e) => this.setState({ state: e.target.value })}>
                                <option value="" disabled selected>State</option>
                                <option value="dummy_test">dummy_test</option>
                                <option value="dummy_test_1">dummy_test_1</option>
                                <option value="dummy_test_2">dummy_test_2</option>
                            </select>
                            <span className="select-dropdown">
                                <img className="main-logo" src={IconDownArrow} alt=""/>
                            </span>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>Zip Code</Form.Label>
                            <Form.Control value={zip} placeholder="Zip Code" onChange={(e) => this.setState({ zip: e.target.value })}></Form.Control>
                        </Form.Group>
                        <button type="submit" style={{"display": "none"}} />
                    </Form>
                </div>
            </div>

            <div className="footer-container"> 
                <button className="secondary" onClick={this.handleArrowBack}>Cancel</button>
                <button className="secondary" onClick={this.handleAddUpdateDealer}>{id ? 'Update' : 'Add'}</button>
            </div>

        </div>
    )
}

const mapStateToProps = state => ({
    selectedDealer: state.admin.selectedDealer
});

const mapDispatchToProps = dispatch => ({
    updateDealer: () => dispatch(updateDealer())
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddDealer);
