import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight, HeaderLogo } from '../../components/Dealer/Header';
import { IconDashboardSales, IconDashboardStaff, IconDashboardSalesApp, iconNotify, IconDashboardPending, IconDashboardApproved, IconDashboardDeclined, IconDashboardFunded, IconDashboardCanceled, IconDashboardPreApprovals, IconDashboardIncomplete, IconSearchInActive, IconSmallArrowRight } from '../../assets/images';

import { searchCustomers } from '../../redux/actions/customers';

class HomeScreen extends Component {

    state = {
        lastName: '',
        city: '',

        searchShow: false
    }

    onSalesApp = () => {
        this.props.history.push('/sales');
    }

    onSales = () => {
        this.props.history.push('/sales-list');
    }

    handleSearchClick = () => {
        // const body = {
        //     last_name: this.state.lastName,
        //     city: this.state.city
        // }
        // this.props.searchCustomers(body);
        this.setState({ searchShow: true });
    }

    handleSearchRowClick = (cifno) => {
        this.props.history.push(`/customer/${cifno}`);
    }

    render() {
        const { lastName, city, searchShow } = this.state;
        // const { customers } = this.props;

        const customers = [
            {

            },
            {

            },
            {

            }
        ]

        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft />
                    <HeaderCenter>
                        <HeaderLogo />
                    </HeaderCenter>
                    <HeaderRight />
                </Header>
                <div className="main">
                    <div className="dashboard">
                        <div className="customer-search">
                            <div id="header">
                                <span>CUSTOMER SEARCH</span>
                                <div id="icon">
                                    <img src={IconSearchInActive} onClick={this.handleSearchClick} />
                                </div>
                            </div>
                            <div id="form">
                                <Form.Group className="mb-18">
                                    <Form.Label>Customer Last Name</Form.Label>
                                    <Form.Control value={lastName} onChange={(e) => this.setState({ lastName: e.target.value })}></Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>City</Form.Label>
                                    <Form.Control value={city} onChange={(e) => this.setState({ city: e.target.value })}></Form.Control>
                                </Form.Group>
                            </div>
                        </div>

                        { !searchShow && (
                            <div className="light-bg">
                                <div className="sales-details">
                                    <div id="box" className="main-btn">
                                        <div className="part1">
                                            <div id="pre-approvals" onClick={() => {this.props.history.push('/preapprovals');}}>
                                                <img src={IconDashboardPreApprovals} />
                                                <div>
                                                    <div className="listing-action">
                                                        PRE-APPROVALS <div className="count">05</div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div id="incomplete">
                                                <img src={IconDashboardIncomplete} />
                                                <div>
                                                    <div className="listing-action">
                                                        INCOMPLETE <div className="count">05</div>
                                                    </div>
                                                    <img className="notify-count" src={iconNotify} alt="" />
                                                </div>
                                            </div>
