import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col, Button, Modal } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft, IconCustomerEdit } from '../../assets/images';

import { changeCustomer } from '../../redux/actions/customer';
import { getCustomerById, sendPreapproval } from '../../redux/actions/customers';

class CustomerDetail extends Component {

    state = {
        modalShow: false
    }

    async componentDidMount() {
        const id = this.props.match.params.id;
        this.props.getCustomerById(id);
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handlePreApprovalClick = async () => {
        const result = await this.props.sendPreapproval(this.props.customer.data.id);
        if (result) {
            this.setState({ modalShow: true });
        }
    }

    handleModalClose = () => {
        this.setState({ modalShow: false });
    }

    handleGenerateOrderClick = () => {
        const customer = this.props.customer.data;
        this.props.changeCustomer({
            order_type: 2,
            main_app: {
                name: customer.name,
                email: customer.email,
                dobY: customer.dobY,
                dobM: customer.dobM,
                dobD: customer.dobD,
                ssn: customer.ssn,
                dl: customer.dl,
                nod: customer.nod,
                cell_phone: customer.cell_phone,
                home_phone: customer.home_phone,
                street: customer.street,
                city: customer.city,
                state: customer.state,
                zip: customer.zip,
                yt1: "",
                own_or_rent: "",
                present_employer: "",
                yt2: "",
                job_title: "",
                employer_phone: "",
                monthly_income: "",
                additional_income: "",
                source: "",
                landlord_holder: "",
                monthly_rent_payment: ""
            },
        });
        this.props.history.push('/contact');
    }

    render() {
        const { loading, data } = this.props.customer;
        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        { loading && <span>Loading...</span> }
                        { !loading && Object.keys(data).length > 0 && <span>{data.name}</span> }
                    </HeaderCenter>
                    <HeaderRight>
                        <img src={IconCustomerEdit} />
                    </HeaderRight>
                </Header>
                { !loading && Object.keys(data).length > 0 && (
                    <div className="main">
                        <div className="customer-details">
                            <div className="address-and-buttons">
                                <div id="address">
                                    <div>{data.address}</div>
                                    <div>{data.zip} {data.city}</div>
                                    <div>{data.state}</div>
                                </div>
                                <div id="buttons">
                                    <div className="button-preapproval" onClick={this.handlePreApprovalClick}>REQUEST PREAPPROVAL</div>
                                    <div className="button-order" onClick={this.handleGenerateOrderClick}>GENERATE ORDER</div>
                                </div>
                            </div>

                            <div className="tab1">Contact</div>
                            <div className="contact-info">
                                <div>
                                    <strong>TCP Customer Since: </strong>
                                    <span>{data.created_at}</span>
                                </div>
                                { data.email && (
                                    <div>
                                        <strong>Email: </strong>
                                        <span>{data.email}</span>
                                    </div>
                                ) }
                                { data.dob && (
                                    <div>
                                        <strong>Date Of Birth: </strong>
                                        <span>{data.dob}</span>
                                    </div>
                                ) }
                                <div>
                                    <strong>TCP Customer Number: </strong>
                                    <span>{data.cif_number}</span>
                                </div>
                                <div className="button-comment">
                                    <div id="button">COMMENT</div>
                                </div>
                            </div>

                            <div className="tab2">Customer History</div>
                            <div className="history-info">
                                { data.apps.map(app => (
                                    <div key={app.id}>
                                        { app.id } [PRODUCT] ORDER { app.created_at }
                                    </div>
                                )) }
                            </div>
                        </div>
                    </div>
                )}

                <Modal show={this.state.modalShow} onHide={this.handleModalClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Status</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>The pre-approval has been sent to admin.</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleModalClose}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    customer: state.customers.selected
});

export default connect(
    mapStateToProps,
    {
        getCustomerById,
        sendPreapproval,
        changeCustomer
    }
)(CustomerDetail);
