import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Form, Modal } from 'react-bootstrap';

import InputMask from 'react-input-mask';

import Header from '../../components/Sales/Header';
import Footer from '../../components/Sales/Footer';

import Header2 from '../../components/Sales/Header2';
import Checkbox, { Radio } from '../../components/Checkbox';

import { states_info } from '../../assets/options';
import { IconHome } from '../../assets/images';

import { changeCustomerContact, changeCustomer, clearCustomer, addProduct } from '../../redux/actions/customer';

class ReorderScreen extends Component {

    state = {
        validated: false,
        backModal: false
    }

    changeState = (data) => {
        this.props.changeCustomer(data);
    }

    changeFormState = (key, value, role = "main") => {
        this.props.changeCustomerContact(key, value, role);
    }

    onNext = () => {
        // const { name } = this.props.customer.contact;
        // if (name === '') {
        //     return;
        // }
        if (this.props.customer.products.length === 0) {
            this.props.addProduct();
        }
        this.props.history.replace('/product/0');
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({ validated: true });

        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.stopPropagation();
        } else {
            this.onNext();
        }
    }

    handleModalClose = () => {
        this.setState({ backModal: false });
    }

    handleModalYes = () => {
        this.props.clearCustomer();
        this.props.history.replace('/sales');
    }

    renderApplicantForm = (app, role) => {
        // const {
        //     name, street, city, state, phone, zip, email,
        //     co_name, same_address, co_street, co_city, co_state, co_phone, co_zip, co_email,
        //     co_enabled, co_complete, co_separate
        // } = this.props.customer.contact;

        return (
            <>
                <Form.Label className="group-label">PERSONAL INFORMATION ewwerwrew</Form.Label>
                <Form.Group>
                    <Form.Label>{ role === "main" ?  "" : "Co-"}Applicant Name</Form.Label>
                    <Form.Label className="form-label-required">required</Form.Label>
                    <Form.Control
                        required
                        value={app.name || ''}
                        onChange={e => this.changeFormState("name", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Date of Birth</Form.Label>
                    <div style={{ display: "flex", flexDirection: "row" }}>
                        <Form.Control
                            type="number"
                            placeholder="MM"
                            value={app.dobM || '' }
                            onChange={e => this.changeFormState("dobM", e.target.value, role)}
                            style={{flex: 1, marginRight: 10 }}   
                        />                            
                        <Form.Control
                            type="number"
                            placeholder="DD"
                            value={app.dobD || '' }
                            onChange={e => this.changeFormState("dobD", e.target.value, role)}
                            style={{ flex: 1, marginRight: 10 }}
                        />
                        <Form.Control
                            type="number"
                            placeholder="YYYY"
                            value={app.dobY || '' }
                            onChange={e => this.changeFormState("dobY", e.target.value, role)}
                            style={{ flex: 2 }}
                        />
                    </div>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Social Security Number</Form.Label>
                    <Form.Control
                        value={app.ssn || ''}
                        onChange={e => this.changeFormState("ssn", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Driver's License #</Form.Label>
                    <Form.Control
                        value={app.dl || ''}
                        onChange={e => this.changeFormState("dl", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>No. of Dependents:</Form.Label>
                    <Form.Control
                        value={app.nod || ''}
                        onChange={e => this.changeFormState("nod", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>{ role === "main" ?  "" : "Co-"}Applicant Email</Form.Label>
                    <Form.Label className="form-label-required">required</Form.Label>
                    <Form.Control
                        required
                        pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
                        type="email"
                        value={app.email || ''}
                        onChange={e => this.changeFormState("email", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Cell Phone Number</Form.Label>
                    <InputMask
                        className="form-control"
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        mask="999-999-9999"
                        maskChar=""
                        value={app.cell_phone || ''}
                        onChange={e => this.changeFormState("cell_phone", e.target.value, role)}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Home Phone Number</Form.Label>
                    <InputMask
                        className="form-control"
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        mask="999-999-9999"
                        maskChar=""
                        value={app.home_phone || ''}
                        onChange={e => this.changeFormState("home_phone", e.target.value, role)}
                    />
                </Form.Group>

                <Form.Label className="group-label">Address</Form.Label>
                <Form.Group>
                    <Form.Label>Street</Form.Label>
                    <Form.Control
                        value={app.street || ''}
                        onChange={e => this.changeFormState("street", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>City</Form.Label>
                    <Form.Control
                        value={app.city || ''}
                        onChange={e => this.changeFormState("city", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>State</Form.Label>
                    <Form.Control
                        as="select"
                        value={app.state || ''}
                        onChange={e => this.changeFormState("state", e.target.value, role) }
                    >
                        { (app.state === undefined || app.state === "") && (
                            <option value="">Choose...</option>
                        )}
                        { states_info.map(info => (
                            <option key={info.abbreviation} value={info.name}>{info.name}</option>
                        )) }
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>ZIP Number</Form.Label>
                    <Form.Control
                        type="number"
                        value={app.zip || ''}
                        pattern="^\d{5}(?:[-\s]\d{4})?$"
                        onChange={e => this.changeFormState("zip", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Years There</Form.Label>
                    <Form.Control
                        value={app.yt1 || ''}
                        onChange={e => this.changeFormState("yt1", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group className="own-rent-radios">
                    <Radio
                        label="Own"
                        checked={app.own_or_rent === "own"}
                        onChangeValue={() => this.changeFormState("own_or_rent", "own", role)}
                    />
                    <Radio
                        label="Rent"
                        checked={app.own_or_rent === "rent"}
                        onChangeValue={() => this.changeFormState("own_or_rent", "rent", role)}
                    />
                </Form.Group>

                <Form.Label className="group-label">FINANCIAL INFORMATION</Form.Label>
                <Form.Group>
                    <Form.Label>Present Employer</Form.Label>
                    <Form.Control
                        value={app.present_employer || ''}
                        onChange={e => this.changeFormState("present_employer", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Years There</Form.Label>
                    <Form.Control
                        value={app.yt2 || ''}
                        onChange={e => this.changeFormState("yt2", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Job Title/Position</Form.Label>
                    <Form.Control
                        value={app.job_title || ''}
                        onChange={e => this.changeFormState("job_title", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Employer's Phone Number</Form.Label>
                    <InputMask
                        className="form-control"
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        mask="999-999-9999"
                        maskChar=""
                        value={app.employer_phone || ''}
                        onChange={e => this.changeFormState("employer_phone", e.target.value, role)}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Monthly Income (pre-tax)</Form.Label>
                    <Form.Control
                        value={app.monthly_income || ''}
                        onChange={e => this.changeFormState("monthly_income", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Additional Income (if any): Per year/mo?</Form.Label>
                    <Form.Control
                        value={app.additional_income || ''}
                        onChange={e => this.changeFormState("additional_income", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Source</Form.Label>
                    <Form.Control
                        value={app.source || ''}
                        onChange={e => this.changeFormState("source", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Landlord or Mortgage Holder</Form.Label>
                    <Form.Control
                        value={app.landlord_holder || ''}
                        onChange={e => this.changeFormState("landlord_holder", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Monthly Rent/Mortgage Payment</Form.Label>
                    <Form.Control
                        value={app.monthly_rent_payment || ''}
                        onChange={e => this.changeFormState("monthly_rent_payment", e.target.value, role) }
                    />
                </Form.Group>
            </>
        )
    }

    render() {
        const validated = this.state.validated;
        const {
            name, street, city, state, phone, zip, email,
            co_name, same_address, co_street, co_city, co_state, co_phone, co_zip, co_email,
            co_enabled, co_complete, co_separate
        } = this.props.customer.contact;
        return (
            <div className="sales">
                <Modal show={this.state.backModal} onHide={this.handleModalClose}>
                    <Modal.Header closeButton></Modal.Header>
                    <Modal.Body>
                        <p>Returning to the Start Screen will cause you to lose all progress on this customer.</p>
                        <p>Do you wish to continue?</p>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleModalClose}>
                            No
                        </Button>
                        <Button variant="primary" onClick={this.handleModalYes}>
                            Yes
                        </Button>
                    </Modal.Footer>
                </Modal>
                <Form noValidate validated={validated} onSubmit={this.handleSubmit}>
                    <Header history={this.props.history}>
                        <img src={IconHome} alt="home" className="icon-home" onClick={() => this.setState({ backModal: true })} />
                    </Header>
                    <div className="main">
                        <Header2
                            id={-1}
                            history={this.props.history}
                        />
                        <div className="container" >
                            { this.renderApplicantForm(this.props.customer.contact.main_app, "main") }
                            <Form.Group>
                                <Checkbox
                                    checked={co_enabled}
                                    onChange={() => {
                                        if (co_enabled) {
                                            this.changeState({
                                                co_enabled: !co_enabled,
                                                same_address: false,
                                                co_street: '',
                                                co_city: '',
                                                co_state: '',
                                                co_zip: ''
                                            });
                                        } else {
                                            this.changeState({co_enabled: !co_enabled});
                                        }
                                    }}
                                    label="Add co-applicant"
                                />
                            </Form.Group>
                            { co_enabled && this.renderApplicantForm(this.props.customer.contact.co_app, "co") }
                            {/* { co_enabled && (
                                <div style={{ marginTop: 30, marginBottom: 20 }}>
                                    <Form.Group>
                                        <Form.Label>Co-applicant Name</Form.Label>
                                        <Form.Label className="form-label-required">required</Form.Label>
                                        <Form.Control
                                            required
                                            value={co_name}
                                            onChange={e => this.changeState({co_name: e.target.value}) }
                                        />
                                    </Form.Group>
                                    <Form.Group className="group-address">
                                        <Form.Label>Address</Form.Label>
                                    </Form.Group>
                                    <Form.Group style={{ marginTop: 10 }}>
                                        <Checkbox
                                            checked={same_address}
                                            onChange={() => {
                                                if (!same_address) {
                                                    this.changeState({
                                                        same_address: !same_address,
                                                        co_street: street,
                                                        co_city: city,
                                                        co_state: state,
                                                        co_zip: zip
                                                    });
                                                } else {
                                                    this.changeState({same_address: !same_address});
                                                }
                                            }}
                                            label="same address"
                                        />
                                    </Form.Group>                                

                                    <Form.Group>
                                        <Form.Label>Street</Form.Label>
                                        <Form.Control
                                            required
                                            value={co_street}
                                            onChange={e => this.changeState({co_street: e.target.value}) }
                                        />
                                    </Form.Group>
                                    <Form.Group>
                                        <Form.Label>City</Form.Label>
                                        <Form.Control
                                            required
                                            value={co_city}
                                            onChange={e => this.changeState({co_city: e.target.value}) }
                                        />
                                    </Form.Group>

                                    <Form.Group>
                                        <Form.Label>State</Form.Label>
                                        <Form.Control
                                            as="select"
                                            required
                                            value={co_state}
                                            onChange={e => this.changeState({co_state: e.target.value}) }
                                        >
                                            { state === "" && (
                                                <option value="">Choose...</option>
                                            )}
                                            { states_info.map(info => (
                                                <option key={info.abbreviation} value={info.name}>{info.name}</option>
                                            )) }
                                        </Form.Control>
                                    </Form.Group>
                                    <Form.Group>
                                        <Form.Label>ZIP Number</Form.Label>
                                        <Form.Control
                                            type="number"
                                            required
                                            value={co_zip}
                                            pattern="^\d{5}(?:[-\s]\d{4})?$"                                    
                                            onChange={e => this.changeState({co_zip: e.target.value}) }
                                        />}
                                    </Form.Group>
                                    <Form.Group>
                                        <Form.Label>Phone</Form.Label>
                                        <InputMask
                                            required
                                            className="form-control"
                                            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                                            mask="999-999-9999"
                                            maskChar=""
                                            value={co_phone}
                                            onChange={e => this.changeState({ co_phone: e.target.value })}
                                        />
                                    </Form.Group>                                
                                </div>
                            )}


                            { co_enabled && (
                                <>
                                    <Form.Group>
                                        <Checkbox
                                            checked={co_complete}
                                            onChange={() => this.changeState({co_complete: !co_complete, co_separate: false})}
                                            label="Completing form for co-applicant"
                                        />
                                    </Form.Group>
                                    <Form.Group style={{ marginTop: 30 }}>
                                        <Form.Label>Co-applicant Email</Form.Label>
                                        <Form.Label className="form-label-required">required</Form.Label>
                                        <Form.Control
                                            style={{ opacity: co_complete && co_separate ? 0.5 : 1 }}
                                            required={!co_complete || !co_separate}
                                            disabled={co_complete && co_separate}
                                            value={co_email}
                                            type="email"
                                            pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
                                            onChange={e => this.changeState({co_email: e.target.value}) }
                                        />
                                    </Form.Group>
                                    <Form.Group>
                                        <Checkbox
                                            checked={co_separate}
                                            onChange={() => this.changeState({co_complete: true, co_separate: !co_separate})}
                                            label="Co-applicant does not have separate email address"
                                        />
                                    </Form.Group>
                                    <Form.Group>
                                        <Form.Label className="co-used-label">*will be used only for collecting e-signatures</Form.Label>
                                    </Form.Group>
                                </>
                            ) } */}
                        </div>
                    </div>
                    <Footer>
                        <Button type="submit" className="button">NEXT</Button>
                    </Footer>
                </Form>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    customer: state.customer
});

export default connect(
    mapStateToProps,
    {
        changeCustomerContact,
        changeCustomer,
        clearCustomer,
        addProduct
    }
)(ReorderScreen);
