import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Form, Modal } from 'react-bootstrap';

import InputMask from 'react-input-mask';

import Header from '../../components/Sales/Header';
import Footer from '../../components/Sales/Footer';

import Header2 from '../../components/Sales/Header2';
import Checkbox, { Radio } from '../../components/Checkbox';

import { states_info } from '../../assets/options';
import { IconHome } from '../../assets/images';

import { changeCustomerContact, changeCustomer, clearCustomer, addProduct } from '../../redux/actions/customer';

class ContactScreen extends Component {

    state = {
        validated: false,
        backModal: false
    }

    changeState = (data) => {
        this.props.changeCustomer(data);
    }

    changeFormState = (key, value, role = "main") => {
        this.props.changeCustomerContact(key, value, role);
    }

    onNext = () => {
        // const { name } = this.props.customer.contact;
        // if (name === '') {
        //     return;
        // }
        if (this.props.customer.products.length === 0) {
            this.props.addProduct();
        }
        this.props.history.replace('/product/0');

    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({ validated: true });

        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.stopPropagation();
        } else {
            this.onNext();
        }
    }

    handleModalClose = () => {
        this.setState({ backModal: false });
    }

    handleModalYes = () => {
        this.props.clearCustomer();
        this.props.history.replace('/');
    }

    renderApplicantForm = (app, role) => {

        return (
            <>
                <Form.Label className="group-label">PERSONAL INFORMATION</Form.Label>
                <Form.Group>
                    <Form.Label>{ role === "main" ?  "" : "Co-"}Applicant Name</Form.Label>
                    <Form.Label className="form-label-required">required</Form.Label>
                    <Form.Control
                        required
                        value={app.name || ''}
                        onChange={e => this.changeFormState("name", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Date of Birth</Form.Label>
                    <div style={{ display: "flex", flexDirection: "row" }}>
                        <Form.Control
                            type="number"
                            placeholder="MM"
                            value={app.dobM || '' }
                            onChange={e => this.changeFormState("dobM", e.target.value, role)}
                            style={{flex: 1, marginRight: 10 }}   
                        />                            
                        <Form.Control
                            type="number"
                            placeholder="DD"
                            value={app.dobD || '' }
                            onChange={e => this.changeFormState("dobD", e.target.value, role)}
                            style={{ flex: 1, marginRight: 10 }}
                        />
                        <Form.Control
                            type="number"
                            placeholder="YYYY"
                            value={app.dobY || '' }
                            onChange={e => this.changeFormState("dobY", e.target.value, role)}
                            style={{ flex: 2 }}
                        />
                    </div>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Social Security Number</Form.Label>
                    <Form.Control
                        value={app.ssn || ''}
