    render() {
        const validated = this.state.validated;
        const {
            name, street, city, state, phone, zip, email,
            co_name, same_address, co_street, co_city, co_state, co_phone, co_zip, co_email,
            co_enabled, co_complete, co_separate
        } = this.props.customer.contact;
        return (
            <div className="sales">
                <Modal show={this.state.backModal} onHide={this.handleModalClose}>
                    <Modal.Header closeButton></Modal.Header>
                    <Modal.Body>
                        <p>Returning to the Start Screen will cause you to lose all progress on this customer.</p>
                        <p>Do you wish to continue?</p>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleModalClose}>
                            No
                        </Button>
                        <Button variant="primary" onClick={this.handleModalYes}>
                            Yes
                        </Button>
                    </Modal.Footer>
                </Modal>
                <Form noValidate validated={validated} onSubmit={this.handleSubmit}>
                    <Header history={this.props.history}>
                        <img src={IconHome} alt="home" className="icon-home" onClick={() => this.setState({ backModal: true })} />
                    </Header>
                    <div className="main">
                        <Header2
                            id={-1}
                            history={this.props.history}
                        />
                        <div className="container" >
                            { this.renderApplicantForm(this.props.customer.contact.main_app, "main") }
                            <Form.Group>
                                <Checkbox
                                    checked={co_enabled}
                                    onChange={() => {
                                        if (co_enabled) {
                                            this.changeState({
                                                co_enabled: !co_enabled,
                                                same_address: false,
                                                co_street: '',
                                                co_city: '',
                                                co_state: '',
                                                co_zip: ''
                                            });
