import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft } from '../../../assets/images';
import Input from '../../../components/commons/input';
import Loader from 'shared/Loader';

import { getFromData } from '../../../components/commons/utility';
import { updateDealer, addDealer } from '../../../redux/actions/admin';

function AddDealer(props) {

    const {
        history,
        dealer,
        addDealer,
        updateDealer,
        actionLoading
    } = props;

    const [validationResult, setValidationResult] = useState(null);

    const handleArrowBack = () => {
        history.push('/admin/dealers')
    }

    const handleSubmit = evt => {

        evt.preventDefault();
        const formData = getFromData(evt);

        setValidationResult(formData.validationResult);

        if(!formData.validationResult) {

            let data = formData.formData

            if(dealer.id) {

                data = {...data, updated_email: data.email, updated_phone: data.phone}
                updateDealer(history, data)
            }
            else
            {
                addDealer(history, data)
            }
        }

    }

    return (
        <div className="dealer">

            { actionLoading && <Loader /> }

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt="" />
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt="" />
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <form onSubmit={(e) => handleSubmit(e)} noValidate>
                {dealer.id && 
                    <Input 
                        name="id" 
                        type="hidden"
                        value={dealer.id} 
                    />
                }
                <div className="container">
                    <div className="styled-form">
                        <Form.Group className="mb-18">
                            <Input 
                                name={ dealer.id ? "company_name" : "dealer_company_name" } 
                                type="text" 
                                defaultValue={dealer.company_name} 
                                label="Dealer Name"
                                defaultText = "Dealer Name"
                                required = {true}
                                error = {{
                                    'empty': "Please enter Dealer Name"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <div className="box">
                            <Form.Group className="mb-18">
                                <Input 
                                    name={ dealer.id ? "email" : "dealer_email" }
                                    type="email"
                                    regex = "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    defaultValue={dealer.email} 
                                    label="Main Dealer User Email"
                                    defaultText = "Main Dealer User Email"
                                    required = {true}
                                    error = {{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Main Dealer User Email"
                                    }}
                                    validationResult = {validationResult}
                                />
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Input 
                                    name={ dealer.id ? "contact_email" : "dealer_contact_email" }
                                    type="email"
                                    regex = "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    defaultValue={dealer.contact_email} 
                                    label="Dealer Contact Email"
                                    defaultText = "Dealer Contact Email"
                                    required = {true}
                                    error = {{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Dealer Contact Email"
                                    }}
                                    validationResult = {validationResult}
                                />
                            </Form.Group>
                        </div>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ dealer.id ? "phone" : "dealer_phone" }
                                type="number"
                                defaultValue={dealer.phone} 
                                label="Phone"
                                defaultText = "Phone"
                                maxLength = {10}
                                required = {true}
                                error = {{
                                    'invalid': "Please enter number only",
                                    'empty': "Please enter Phone number"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <span className="divider"></span>
                        <span className="title">ADDRESS</span>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ dealer.id ? "street" : "dealer_address_street" }
                                type="text"
                                defaultValue={dealer.street} 
                                label="Street"
                                defaultText = "Street"
                                required = {true}
                                error = {{
                                    'empty': "Please enter Street"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ dealer.id ? "city" : "dealer_address_city" } 
                                type="text"
                                defaultValue={dealer.city} 
                                label="City"
                                defaultText = "City"
                                required = {true}
                                error = {{
                                    'empty': "Please enter city"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>State</Form.Label>
                            <select 
                                name={ dealer.id ? "state" : "dealer_address_state" }
                                defaultValue={dealer.state ? dealer.state : ''} 
                                placeholder="State"
                            >
                                <option value="" disabled>State</option>
                                <option value="dummy_test">dummy_test</option>
                                <option value="dummy_test_1">dummy_test_1</option>
                                <option value="dummy_test_2">dummy_test_2</option>
                            </select>
                            <span className="select-dropdown">
                                <img className="main-logo" src={IconDownArrow} alt="" />
                            </span>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ dealer.id ? "zip" : "dealer_address_zipcode" }
                                type="number"
                                defaultValue={dealer.zip} 
                                label="Zip Code"
                                defaultText = "Zip Code"
                                maxLength = {6}
                                required = {true}
                                error = {{
                                    'empty': "Please enter zip code"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                    </div>
                </div>
                <div className="footer-container">
                    <button className="secondary" onClick={() => handleArrowBack()}>Cancel</button>
                    <button className="secondary" type="submit">{dealer.id ? 'Update' : 'Add'}</button>
                </div>
            </form>

        </div>
    )
}

const mapStateToProps = state => ({
    dealer: state.admin.selectedDealer,
    actionLoading: state.admin.actionLoading
});

const mapDispatchToProps = dispatch => ({
    addDealer: (history, data) => dispatch(addDealer(history, data)),
    updateDealer: (history, data) => dispatch(updateDealer(history, data))
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddDealer);
