import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header from '../../components/Sales/Header';
import CustomerModalList from 'shared/CustomerModalList';

import { IconDashboardSales, IconHome, IconDashboardStaff, IconDashboardSalesApp, iconNotify, IconDashboardPending, IconDashboardApproved, IconDashboardDeclined, IconDashboardFunded, IconDashboardCanceled, IconDashboardPreApprovals, IconDashboardIncomplete, IconSearchInActive, IconSmallArrowRight } from '../../assets/images';
import { getSalesList } from '../../redux/actions/sales';
import { searchCustomers, getPreApprovalCustomers } from '../../redux/actions/customers';

class HomeScreen extends Component {

    state = {
        lastName: '',
        city: '',

        searchShow: false
    }

    componentDidMount() {
        this.props.getPreApprovalCustomers();
        this.props.getSalesList();
    }

    onSalesApp = () => {
        this.props.history.push('/sales');
    }

    onSales = (status) => {
        this.props.history.push(`/sales-list/${status}`);
    }

    handleSearchClick = () => {
        const body = {
            last_name: this.state.lastName,
            city: this.state.city
        }
        this.props.searchCustomers(body);
        this.setState({ searchShow: true });
    }

    handleSearchRowClick = (cifno) => {
        const { history } = this.props;
        history.push(`/customer/${cifno.id}`);
    }

    hideSearchBox = () => {
        this.setState({
            searchShow: false
        })
    }

    render() {
        const { lastName, city, searchShow } = this.state;
        const { customers } = this.props;

        let apps = this.props.apps;

        const preApprovalCount = this.props.preAprrovalCustomer.length;
        const incompleteCount = apps.filter(a => (a.hello_sign && a.hello_sign.length > 0 && (a.hello_sign[0].status_code === "declined" || a.hello_sign[0].status_code === "error_unknown" || a.hello_sign[0].status_code === "error_file" || a.hello_sign[0].status_code === "error_component_position" || a.hello_sign[0].status_code === "error_text_tag" || a.hello_sign[0].last_viewed_at === null || a.hello_sign[0].status_code === "awaiting_signature"))).length;
        const pendingCount = apps.filter(a => (a.status === 'waiting' && a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed")).length;
        const declinedCount = apps.filter(a => (a.status === 'declined' && a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed")).length;
        const approvedCount = apps.filter(a => (a.status === 'approved' && a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed")).length;
        const fundedCount = apps.filter(a => (a.status === 'funded' && a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed")).length;
        const canceledCount = apps.filter(a => (a.status === 'canceled' && a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed")).length;


        return (
            <div className="dealer sales">
                <Header isHome={true} history={this.props.history} avatar={this.props.avatar} className="home-header"></Header>
                <div className="main">
                    <div className="dashboard">
                        <div className="customer-search">
                            <div id="header">
                                <span>CUSTOMER SEARCH</span>
                                <div id="icon">
                                    <img src={IconSearchInActive} onClick={this.handleSearchClick} alt=""/>
                                </div>
                            </div>
                            <div id="form">
                                <Form.Group className="mb-18">
                                    <Form.Label>Customer Last Name</Form.Label>
                                    <Form.Control value={lastName} onChange={(e) => this.setState({ lastName: e.target.value })}></Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>City</Form.Label>
                                    <Form.Control value={city} onChange={(e) => this.setState({ city: e.target.value })}></Form.Control>
                                </Form.Group>
                            </div>
                        </div>

                        { !searchShow && (
                            <div className="light-bg">
                                <div className="sales-details">
                                    <div id="box" className="main-btn">
                                        <div className="part1">
                                            <div id="pre-approvals" onClick={() => {this.props.history.push('/preapprovals');}}>
                                                <img src={IconDashboardPreApprovals} alt="" />
                                                <div>
                                                    <div className="listing-action">
                                                        PRE-APPROVALS <div className="count">{preApprovalCount}</div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div id="incomplete" onClick={() => {this.props.history.push('/incompletes');}}>
                                                <img src={IconDashboardIncomplete} alt="" />
                                                <div>
                                                    <div className="listing-action">
                                                        INCOMPLETE <div className="count">{incompleteCount}</div>
                                                    </div>
                                                    {incompleteCount > 0 && <img className="notify-count" src={iconNotify} alt="" />}
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    <div id="box">
                                        <div className="part2">
                                            <div>
                                            <div className="count">{pendingCount}</div>
                                                <img className="icon" src={IconDashboardPending} onClick={()=>{this.onSales('waiting')}} alt=""/>
                                                <div className="status">Pending</div>
                                            </div>
                                            <div>
                                            <div className="count">{declinedCount}</div>
                                                <img className="icon" src={IconDashboardDeclined} onClick={()=>{this.onSales('declined')}} alt="" />
                                                <div className="status">Declined</div>
                                            </div>
                                            <div>
                                            <div className="count">{approvedCount}</div>
                                                <img className="icon" src={IconDashboardApproved} onClick={()=>{this.onSales('approved')}} alt="" />
                                                <div className="status">Approved</div>
                                            </div>
                                            <div>
                                            <div className="count">{fundedCount}</div>
                                                <img className="icon" src={IconDashboardFunded} onClick={()=>{this.onSales('funded')}} alt="" />
                                                <div className="status">Funded</div>
                                            </div>
                                            <div>
                                            <div className="count">{canceledCount}</div>
                                                <img className="icon" src={IconDashboardCanceled} onClick={()=>{this.onSales('canceled')}} alt=""/>
                                                <div className="status">Canceled</div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="action-button" onClick={()=>{this.onSales('all')}}>
                                        <div id="icon">
                                            <img src={IconDashboardSalesApp} alt=""/>
                                        </div>
                                        <div id="title">
                                            <span>SALES</span>
                                        </div>
                                    </div>
                                </div>

                                <Row className="actions">
                                    <Col md="6" className="action-button pl-0 pr-2">
                                        <div id="icon">
                                            <img src={IconDashboardStaff} alt="" />
                                        </div>
                                        <div id="title">STAFF MANAGEMENT</div>
                                    </Col>
                                    <Col md="6" className="action-button pr-0 pl-2" onClick={this.onSalesApp}>
                                        <div id="icon">
                                            <img src={IconDashboardSales} alt=""/>
                                        </div>
                                        <div id="title">SALES APP</div>
                                    </Col>
                                </Row>
                            </div>
                        )}

                    </div>
                </div>
                {
                    searchShow && 
                        <CustomerModalList
                        customers={ customers }
                        setCancel={ this.hideSearchBox }
                        handleSearchRowClick={ this.handleSearchRowClick } />
                }   
                <div className="cmp-info">
                    2020 Travis Capital Partners
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    apps: state.sales.apps,
    avatar: state.auth.avatar,
    customers: state.customers,
    preAprrovalCustomer: state.customers.preAprrovalCustomer
});

export default connect(
    mapStateToProps,
    {
        getPreApprovalCustomers,
        getSalesList,
        searchCustomers
    }
)(HomeScreen);
