import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconAdd, IconArrowLeft, IconDeleteNew, IconPreapprovalWaiting, IconPreapprovalDeclined, IconStatusConfirmed } from '../../../assets/images';

import { setSelectedDealerRequest, getDealersRequests } from '../../../redux/actions/admin';
import Loader from 'shared/Loader';




function DealerManagements(props) {

    const {
        history,
        dealers,
        getDealersRequests,
        setSelectedDealerRequest
    } = props;

    const IconSmallArrowRight = (props) => {
        return (
            <svg className={`arrow-icon ${props.id === openDealerIndex && "arrow-icon-active"}`} enableBackground="new 0 0 12 12" height="12px" id="Layer_1" version="1.1" viewBox="0 0 32 32" width="32px">
                <path d="M24.291,14.276L14.705,4.69c-0.878-0.878-2.317-0.878-3.195,0l-0.8,0.8c-0.878,0.877-0.878,2.316,0,3.194  L18.024,16l-7.315,7.315c-0.878,0.878-0.878,2.317,0,3.194l0.8,0.8c0.878,0.879,2.317,0.879,3.195,0l9.586-9.587  c0.472-0.471,0.682-1.103,0.647-1.723C24.973,15.38,24.763,14.748,24.291,14.276z"/>
            </svg>
        )
    }

    useEffect(() => {
        getDealersRequests();
    }, []);


    // state = {
    //     seachKeyword: '',
    //     openDealerIndex: null,
    //     dealers: this.props.dealers,
    //     filterDealer: this.props.dealers ? this.props.dealers.data : []
    // }

    const handleArrowBack = () => {       
        const { history } = this.props;
        history.push('/')
    }

    const handleAddDealer = () => {       
        setSelectedDealerRequest({})
        history.push(`/admin/addDealer`);
    }

    const handleEditDealer = (item) => {       
        history.push(`/admin/addDealer`);
        setSelectedDealerRequest(item)
    }

    const searchDealer = (key) => {
        // this.setState({ seachKeyword: key });
        // if(key != "")
        // {
        //     let tempFilterDealer = this.props.dealers.data.filter(item => item.company_name.toLowerCase().includes(key.toLowerCase()))
        //     this.setState({ filterDealer: tempFilterDealer });
        // }
        // else
        // {
        //     this.setState({ filterDealer: this.props.dealers.data });
        // }
    }

    const handleOpenDealerAction = (dealer) => {
        // if('map'+dealer.id === this.state.openDealerIndex) {
        //     this.setState({
        //         openDealerIndex: null,
        //     });
        // } else {
        //     this.setState({
        //         openDealerIndex: 'map'+dealer.id,
        //     });
        // }
    };


    return (
        <div className="dealer">
            {
                dealers.loading && <Loader />
            }
            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={this.handleArrowBack} alt=""/>
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt=""/>
                    </div>
                </HeaderCenter>
                <HeaderRight>
                    <img src={IconAdd} onClick={this.handleAddDealer} alt=""/>
                </HeaderRight>
            </Header>
            <div className="search-header">
                <Form onSubmit={this.handleSearchClick}>     
                    <div id="form">
                        <Form.Group className="mb-18">
                            <Form.Control value={seachKeyword} placeholder="Search" onChange={(e) => searchDealer(e.target.value)}></Form.Control>
                        </Form.Group>
                    </div>
                    <button type="submit" style={{"display": "none"}} />
                </Form>
            </div>
            <div className="main">
                    <div className="list">
                    { filterDealer && filterDealer.map((item) => (
                        <>
                            <Row key={item.id} className="single-row" onClick={() => handleOpenDealerAction(item)}>
                                <div className="dealer-row">
                                    <Col xs={6}>
                                        <span className="dealer-name">{item.company_name}</span>
                                    </Col>
                                    <Col xs={6} className="dealer-action">
                                        <span><IconSmallArrowRight id={ 'map'+item.id }/></span>
                                    </Col>
                                </div>
                                { (openDealerIndex ===  ('map'+item.id)) && (
                                    <Col key={item.id} xs={12} className="single-row-details">
                                        <button className="delete">
                                            <img src={IconDeleteNew} alt=""/> Delete
                                        </button>
                                        <button className="edit" onClick={() => handleEditDealer(item)}>Edit</button>
                                    </Col>
                                )}
                            </Row>
                        </>
                    ))}
                </div>
            </div>
        </div>
    )

}

const mapStateToProps = state => ({
    dealers: state.admin.dealers
});

const mapDispatchToProps = dispatch => ({
    getDealersRequests: () => dispatch(getDealersRequests()),
    setSelectedDealerRequest: () => dispatch(setSelectedDealerRequest())
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(DealerManagements);
