import {
    SALES_LIST,
    GET_APP_DETAIL,
    GET_APP_DETAIL_SUCCESS,
    GET_APP_DETAIL_ERROR
} from '../actions/sales';

const INIT_STATE = {
    apps: [],

    selectedApp: {
        loading: false,
        data: {}
    }
};

export default function(state = INIT_STATE, action){
    switch (action.type) {
        case SALES_LIST:
            return {
                ...state,
                apps: action.payload
            }
        case GET_APP_DETAIL:
            return {
                ...state,
                selectedApp: {                    
                    loading: true,
                    data: {}
                }
            }
        case GET_APP_DETAIL_SUCCESS:
            return {
                ...state,
                selectedApp: {
                    loading: false,
                    data: action.payload
                }
            }
        case GET_APP_DETAIL_ERROR:
            return {
                ...state,
                selectedApp: {
                    loading: false,
                    data: {}
                }
            }
        default:
            return state;
    }
}
