import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Form, Modal, Tooltip, OverlayTrigger } from 'react-bootstrap';

import InputMask from 'react-input-mask';
import CustomerModalList from 'shared/CustomerModalList';
import SearchBox from 'shared/SearchBox';

import Header from '../../components/Sales/Header';
import Footer from '../../components/Sales/Footer';

import Header2 from '../../components/Sales/Header2';
import Checkbox, { Radio } from '../../components/Checkbox';

import { FormFieldsContainer, SearchFieldContainer, CancelFormContainer } from './style';
import { states_info } from '../../assets/options';
import { IconHome } from '../../assets/images';

import { 
    changeCustomerContact, 
    changeCustomer, 
    clearCustomer, 
    sendPrequalify,
    setReorderCustomerById,
    sendAll
} from '../../redux/actions/customer';
import { searchCustomers } from '../../redux/actions/customers';
import Loader from 'shared/Loader';

class ReorderScreen extends Component {

    state = {
        validated: false,
        sentModal: false,
        nameSearch: '',
        citySearch: '',
        searchShow: false,
        displayFields: false
    }

    changeState = (data) => {
        this.props.changeCustomer(data);
    }

    changeFormState = (key, value, role = "main") => {
        this.props.changeCustomerContact(key, value, role);
    }

    onSend = () => {
        // this.props.sendPrequalify(this.props.customer);
        const { sendAll, customer } = this.props;
        sendAll(customer, this.handleModalClose);
        this.setState({ sentModal: true });
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({ validated: true });

        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.stopPropagation();
        } else {
            this.onSend();
        }
    }

    handleModalClose = () => {
        this.setState({ sentModal: false });

        const role = localStorage.getItem('role');

        if(role && role.indexOf('dealer') !== -1) {
            this.props.history.replace('/sales');
        } else if(role && role.indexOf('sales') !== -1) {
            this.props.history.replace('/');
        } else {
            this.props.history.replace('/');
        }
    }

    renderApplicantForm = (app, role) => {
        return (
            <>
                <Form.Label className="group-label">PERSONAL INFORMATION</Form.Label>
                <Form.Group>
                    <Form.Label>{ role === "main" ?  "" : "Co-"}Applicant Name</Form.Label>
                    <Form.Label className="form-label-required">required</Form.Label>
                    <Form.Control
                        required
                        value={app.name || ''}
                        onChange={e => this.changeFormState("name", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Date of Birth</Form.Label>
                    <div style={{ display: "flex", flexDirection: "row" }}>
                        <Form.Control
                            type="number"
                            placeholder="MM"
                            value={app.dobM || '' }
                            onChange={e => this.changeFormState("dobM", e.target.value, role)}
                            style={{flex: 1, marginRight: 10 }}   
                        />                            
                        <Form.Control
                            type="number"
                            placeholder="DD"
                            value={app.dobD || '' }
                            onChange={e => this.changeFormState("dobD", e.target.value, role)}
                            style={{ flex: 1, marginRight: 10 }}
                        />
                        <Form.Control
                            type="number"
                            placeholder="YYYY"
                            value={app.dobY || '' }
                            onChange={e => this.changeFormState("dobY", e.target.value, role)}
                            style={{ flex: 2 }}
                        />
                    </div>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Social Security Number</Form.Label>
                    <Form.Control
                        value={app.ssn || ''}
                        onChange={e => this.changeFormState("ssn", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Driver's License #</Form.Label>
                    <Form.Control
                        value={app.dl || ''}
                        onChange={e => this.changeFormState("dl", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>No. of Dependents:</Form.Label>
                    <Form.Control
                        value={app.nod || ''}
                        onChange={e => this.changeFormState("nod", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>{ role === "main" ?  "" : "Co-"}Applicant Email</Form.Label>
                    <Form.Label className="form-label-required">required</Form.Label>
                    <Form.Control
                        required
                        pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
                        type="email"
                        value={app.email || ''}
                        onChange={e => this.changeFormState("email", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Cell Phone Number</Form.Label>
                    <InputMask
                        className="form-control"
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        mask="999-999-9999"
                        maskChar=""
                        value={app.cell_phone || ''}
                        onChange={e => this.changeFormState("cell_phone", e.target.value, role)}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Home Phone Number</Form.Label>
                    <InputMask
                        className="form-control"
                        pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                        mask="999-999-9999"
                        maskChar=""
                        value={app.home_phone || ''}
                        onChange={e => this.changeFormState("home_phone", e.target.value, role)}
                    />
                </Form.Group>

                <Form.Label className="group-label">Address</Form.Label>
                <Form.Group>
                    <Form.Label>Street</Form.Label>
                    <Form.Control
                        value={app.street || ''}
                        onChange={e => this.changeFormState("street", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>City</Form.Label>
                    <Form.Control
                        value={app.city || ''}
                        onChange={e => this.changeFormState("city", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>State</Form.Label>
                    <Form.Control
                        as="select"
                        value={app.state || ''}
                        onChange={e => this.changeFormState("state", e.target.value, role) }
                    >
                        { (app.state === undefined || app.state === "") && (
                            <option value="">Choose...</option>
                        )}
                        { states_info.map(info => (
                            <option key={info.abbreviation} value={info.name}>{info.name}</option>
                        )) }
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>ZIP Number</Form.Label>
                    <Form.Control
                        type="number"
                        value={app.zip || ''}
                        pattern="^\d{5}(?:[-\s]\d{4})?$"
                        onChange={e => this.changeFormState("zip", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Years There</Form.Label>
                    <Form.Control
                        value={app.yt1 || ''}
                        onChange={e => this.changeFormState("yt1", e.target.value, role) }
                    />
                </Form.Group>
                <Form.Group className="own-rent-radios">
                    <Radio
                        label="Own"
                        checked={app.own_or_rent === "own"}
                        onChangeValue={() => this.changeFormState("own_or_rent", "own", role)}
                    />
                    <Radio
                        label="Rent"
                        checked={app.own_or_rent === "rent"}
                        onChangeValue={() => this.changeFormState("own_or_rent", "rent", role)}
                    />
                </Form.Group>
            </>
        )
    }

    handleSearchClick = () => {
        const { nameSearch, citySearch } = this.state;
        const { searchCustomers } = this.props;
        const body = {
            name: nameSearch,
            city: citySearch
        }
        searchCustomers(body);
        this.setState({ searchShow: true });
    }

    hideSearchBox = () => {
        this.setState({
            searchShow: false
        })
    }

    hideFormFields = () => {
        this.setState({
            displayFields: false,
            searchShow: true
        })
    }

    handleSearchRowClick = async (item) => {
        const { setReorderCustomerById } = this.props
        this.setState({
            displayFields: true
        })
        setReorderCustomerById(item.id, this.hideSearchBox, this.hideFormFields);
    }

    renderTooltip(props){
        return  <Tooltip id="button-tooltip" {...props}>
        Cancel
      </Tooltip>
    }

    render() {
        const validated = this.state.validated;
        const { nameSearch, searchShow, citySearch, displayFields } = this.state;
        const { customers } = this.props;
        const { co_enabled } = this.props.customer.contact;
        const { isSettingCustomerDetails, isCustomerFormLoading } = this.props.customer;
        return (
            <div className="sales">
                {
                    (isSettingCustomerDetails || isCustomerFormLoading) && <Loader />
                }
                <Header history={this.props.history}>
                    <img src={IconHome} alt="home" className="icon-home" onClick={() => {this.props.history.replace('/');}} />
                </Header>

                <Header2
                    id={-1}
                    history={this.props.history}
                />
                {/* <Modal show={this.state.sentModal} onHide={this.handleModalClose}>
                    <Modal.Header closeButton></Modal.Header>
                    <Modal.Body>Forms have been sent successfully.</Modal.Body>
                    <Modal.Footer>
                    <Button variant="secondary" onClick={this.handleModalClose}>
                        Close
                    </Button>
                    </Modal.Footer>
                </Modal> */}
                {
                    !displayFields &&   
                    <div className="main">                        
                        <SearchFieldContainer className="container pb-0">                             
                            <SearchBox 
                                nameSearch={ nameSearch }
                                citySearch={ citySearch }
                                nameChangeEvent={ (e) => this.setState({ nameSearch: e.target.value }) }
                                cityChangeEvent={ (e) => this.setState({ citySearch: e.target.value })}
                                handleSearchClick={ this.handleSearchClick} />
                        </SearchFieldContainer>
                    </div>
                }   

                {
                displayFields &&   
                <FormFieldsContainer> 
                    <Form noValidate validated={validated} onSubmit={this.handleSubmit}>
                        <div className="main">                            
                            <div className="container">
                                <CancelFormContainer>
                                    <OverlayTrigger
                                        placement="left"
                                        delay={{ show: 250, hide: 400 }}
                                        overlay={this.renderTooltip}
                                    >
                                        <i className="fa fa-close" onClick={ this.hideFormFields }></i>
                                    </OverlayTrigger>                                    
                                </CancelFormContainer>


                                { this.renderApplicantForm(this.props.customer.contact.main_app, "main") }
                                <Form.Group>
                                    <Checkbox
                                        checked={co_enabled}
                                        onChange={() => {
                                            if (co_enabled) {
                                                this.changeState({
                                                    co_enabled: !co_enabled,
                                                    same_address: false,
                                                    co_street: '',
                                                    co_city: '',
                                                    co_state: '',
                                                    co_zip: ''
                                                });
                                            } else {
                                                this.changeState({co_enabled: !co_enabled});
                                            }
                                        }}
                                        label="Add co-applicant"
                                    />
                                </Form.Group>
                                { co_enabled && this.renderApplicantForm(this.props.customer.contact.co_app, "co") }
                            </div>
                        </div>
                        <Footer>
                            <Button type="submit" className="button">SEND</Button>
                        </Footer>
                    </Form>                      
                </FormFieldsContainer>
                }
                {
                    searchShow && 
                        <CustomerModalList
                        customers={ customers }
                        setCancel={ this.hideSearchBox }
                        handleSearchRowClick={ this.handleSearchRowClick} />
                }                
            </div>
        )
    }
}

const mapStateToProps = state => ({
    customer: state.customer,
    customers: state.customers,
});

export default connect(
    mapStateToProps,
    {
        changeCustomerContact,
        changeCustomer,
        clearCustomer,
        sendPrequalify,
        searchCustomers,
        setReorderCustomerById,
        sendAll
    }
)(ReorderScreen);
