import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Form, Row, Col, Button } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconFixIssueAwesomePaperPlane, IconFixIssueEnvelopeOpen, IconFixIssueAwesomePenFancy, IconFixIssueAwesomeSendRed, IconArrowLeft, IconFixIssueEnvelopeClosed, IconFixIssueAwesomePenFancyRight } from '../../assets/images';
import { getSalesList, getAppDetailById } from '../../redux/actions/sales';

class DealerFix extends Component {

    state = {
    }

    componentDidMount() {
        this.props.getAppDetailById(this.props.match.params.appId);
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    render() {

        const customer = this.props.app;

        console.log(customer)

        return (
            <>
                <div className="fix-issue-section">
                    <Header>
                        <HeaderLeft>
                            <img src={IconArrowLeft} onClick={this.handleArrowBack} height="28"/>
                        </HeaderLeft>
                        <HeaderCenter>
                            <span>FIX ISSUE</span>
                        </HeaderCenter>
                        <HeaderRight>
                        </HeaderRight>
                    </Header>
                    {customer.data.applicant && (
                        <div className="fix-issue-main">
                            <Row>
                                <Col xs={6} className="action-icons">
                                    <Button variant="default"><img src={IconFixIssueAwesomePaperPlane} /></Button>
                                    <Button variant="default"><img src={IconFixIssueEnvelopeOpen} /></Button>
                                    <Button variant="default"><img src={IconFixIssueAwesomePenFancy} /></Button>
                                </Col>
                                <Col xs={6} className="action-icons border-left">
                                    <Button variant="default"><img src={IconFixIssueAwesomeSendRed} /></Button>
                                    <Button variant="default"><img src={IconFixIssueEnvelopeClosed} /></Button>
                                    <Button variant="default"><img src={IconFixIssueAwesomePenFancyRight} /></Button>
                                </Col>
                            </Row>
                            <Row>
                                <Col xs={12} className="customer-item-details">
                                    <div className="name">
                                        {customer.data.applicant.name}
                                    </div>
                                    <div className="address">
                                        {customer.data.applicant.address}
                                        {customer.data.applicant.city}
                                        <br/>
                                        {customer.data.applicant.zip}
                                        {customer.data.applicant.state}
                                    </div>
                                    <div className="item">
                                        Item Purchased
                                    </div>
                                </Col>
                            </Row>
                            <Row className="input-section">
                                <Col xs={12} className="customer-input">
                                    <Form.Group className="mb-18">
                                        <Form.Label>Applicant Email</Form.Label>
                                        <Form.Control
                                            placeholder="customer@email.com"
                                            value={customer.data.applicant.email}
                                            onChange={(e) => this.setState({ customerEmail: e.target.value })}
                                        />
                                    </Form.Group>
                                </Col>
                                <Col xs={12} className="customer-input">
                                    <Form.Group className="mb-18">
                                        <Form.Label>Co-applicant Email</Form.Label>
                                        <Form.Control
                                            placeholder="customer@email.com"
                                            value={customer.data.applicant.email}
                                            onChange={(e) => this.setState({ customerEmail: e.target.value })}
                                        />
                                    </Form.Group>
                                </Col>
                                <Col xs={12} className="customer-action">                            
                                    <Button variant="primary">RESEND EMAIL</Button>
                                    <Button variant="primary">MODIFY ORDER</Button>
                                </Col>
                            </Row>
                        </div>  
                    )}          
                </div>
                <Row className="footer-section">                        
                    <Col xs={12} className="footer-action">                            
                        <Button variant="primary">SAVE</Button>
                    </Col>
                </Row>
            </>
        )
    }
}

const mapStateToProps = state => ({
    app: state.sales.selectedApp
});

export default connect(
    mapStateToProps,
    {
        getAppDetailById
    }
)(DealerFix);
