import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft } from '../../../assets/images';
import { renderField } from 'utils/formUtils';
import { updateDealer } from '../../../redux/actions/admin';

function AddDealer(props) {

    const {
        history,
        selectedDealer
    } = props;

    const emptyObj = {
        id: null,
        company_name: null,
        email: null,
        contact_email: null,
        phone: null,
        street: null,
        city: null,
        state: null,
        zip: null,
    }

    const [dealer, setDealer] = useState(selectedDealer ? selectedDealer : emptyObj);
    const [validationResult, setValidationResult] = useState(null);

    const handleArrowBack = () => {
        history.push('/admin/dealers')
    }

    const handleSubmit = evt => {

        // evt.preventDefault();
        // const formData = getFormData(evt)

    }

    return (
        <div className="dealer">

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt="" />
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt="" />
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <Form onSubmit={(e) => handleSubmit(e)} noValidate>
                <div className="container">
                    <div className="styled-form">

                    <Field
                        name="email"
                        label="Email"
                        type="text"
                        component={ renderField }
                        maxLength="150"
                    />
                    </div>
                </div>
                <div className="footer-container">
                    <button className="secondary" onClick={() => handleArrowBack()}>Cancel</button>
                    <button className="secondary" type="submit">{dealer.id ? 'Update' : 'Add'}</button>
                </div>
            </Form>

        </div>
    )
}

const mapStateToProps = state => ({
    selectedDealer: state.admin.selectedDealer
});

const mapDispatchToProps = dispatch => ({
    updateDealer: () => dispatch(updateDealer())
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddDealer);
