import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Row, Col, Button } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft } from '../../assets/images';

import { changeSelectedPendingApplicationStatus } from '../../redux/actions/admin';

class PreApprovalItem extends Component {

    componentDidMount() {
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleApproveClick = () => {
        this.props.changeSelectedPendingApplicationStatus(this.props.item.id, "approved");
        this.props.history.push(`/admin/pendingApplications`);
    }

    handleDeclineClick = () => {
        this.props.changeSelectedPendingApplicationStatus(this.props.item.id, "declined");
        this.props.history.push(`/admin/pendingApplications`);
    }

    render() {
        const { item } = this.props;
        if (Object.keys(item).length === 0) {
            return (
                <div>

                </div>
            )
        }

        console.log(item)

        const { id, created_at, status } = item;

        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        <span>{item.name}</span>
                    </HeaderCenter>
                    <HeaderRight>

                    </HeaderRight>
                </Header>
                <div className="main">
                    <div className="customer-details">
                        <div className="contact-info">
                                <div>
                                    <strong>Customer Name: </strong>
                                    <span>{item.name}</span>
                                </div>
                            <div>
                                <strong>PreApproval Created At: </strong>
                                <span>{created_at}</span>
                            </div>
                            { item.email && (
                                <div>
                                    <strong>Customer Email: </strong>
                                    <span>{item.email}</span>
                                </div>
                            ) }
                            <div>                                
                                <strong>Item Request: </strong>
                                { item.products.map(product => (
                                    <div key={product.id}>{ product.type }, ${product.balance}</div>
                                )) }
                            </div>
                            <div style={{ marginTop: 20, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                                { status === "waiting" && (
                                    <>
                                        <Button
                                            style={{ marginRight: 20 }}
                                            variant="success"
                                            onClick={this.handleApproveClick}
                                        >
                                            Approve
                                        </Button>
                                        <Button
                                            variant="danger"
                                            onClick={this.handleDeclineClick}
                                        >
                                            Decline
                                        </Button>
                                    </>
                                ) }

                                { status === "approved" && (
                                    <div style={{ color: '#009e87', fontSize: 16, fontWeight: 'bold' }}>
                                        Preapproval accepted
                                    </div>
                                ) }

                                { status === "declined" && (
                                    <div style={{ color: '#d81740', fontSize: 16, fontWeight: 'bold' }}>
                                        Preapproval declined
                                    </div>
                                ) }
                            </div>
                        </div>                        
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    item: state.admin.selectedPendingApplication
});

export default connect(
    mapStateToProps,
    {
        changeSelectedPendingApplicationStatus
    }
)(PreApprovalItem);
