        monthly_income: "",
        additional_income: "",
        source: "",
        landlord_holder: "",
        monthly_rent_payment: ""
    },

    name: '',
    street: '',
    city: '',
    state: '',
    zip: '',
    phone: '',
    email: '',

    co_name: '',
    same_address: false,
    co_street: '',
    co_city: '',
    co_state: '',
    co_zip: '',
    co_phone: '',
    co_email: '',

    co_enabled: false,
    co_complete: false,
    co_separate: false,

    sent: false
};

export const INITIAL_PRODUCT = {
    "product_type": "FOOD",
    "price": "1000",
    "total_discount": 0,
    "coupon": 0,
    "add_discount": 0,
    "tax": 0,
    "cash_credit": "100",
    "check": 0,
    "finance_period": 2,
    "makemodel": "1                 1                 1",
    "saved": true
    }

const INIT_STATE = {
    contact: INITIAL_CONTACT,
    products: []
};

export default function(state = INIT_STATE, action){
    var products;
    switch (action.type) {
        case CHANGE_CONTACT:
            if (action.payload.role === "main") {
                return {
                    ...state,
                    contact: {
                        ...state.contact,
                        main_app: {
                            ...state.contact.main_app,
                            [action.payload.key]: action.payload.value
                        }
                    }
                }    
            } else if (action.payload.role === "co") {
                return {
                    ...state,
                    contact: {
                        ...state.contact,
                        co_app: {
                            ...state.contact.co_app,
                            [action.payload.key]: action.payload.value
                        }
                    }
                }
            }
            return {
                ...state,
                contact: {
                    ...state.contact,
                    ...action.payload
                }
            }
        case CLEAR_CUSTOMER:
            return INIT_STATE;
        case ADD_PRODUCT:
            return {
                ...state,
                products: [
                    ...state.products,
                    INITIAL_PRODUCT
                ]
            };
        case CHANGE_PRODUCT:
            products = state.products;
            for (var i = 0; i < products.length; i ++) {
                if (i === action.payload.id) {
                    products[i] = {
                        ...products[i],
                        ...action.payload.data
                    };
                }
            }
            return {
                ...state,
                products: products
            };
        case REMOVE_PRODUCT:
            products = state.products;
            products.splice(action.payload.id, 1);
            return {
                ...state,
                products: products
            };
        default:
            return state;
    }
}
