import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Form, Row, Col, Button } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft } from '../../assets/images';
import { resendApplicationMail, getAppDetailById } from '../../redux/actions/sales';

class DealerFix extends Component {

    state = {
        customerEmail: null,
        coCustomerEmail: null,
        confireModal: false
    }

    componentDidMount() {
        this.props.getAppDetailById(this.props.match.params.appId);
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleResendMailAction = () => {
        const customerEmail = this.state.customerEmail ? this.state.customerEmail : this.props.app.data.applicant.email;
        const coCustomerEmail = this.state.coCustomerEmail ? this.state.coCustomerEmail : this.props.app.data.co_applicant ? this.props.app.data.co_applicant.email : null;
        this.props.resendApplicationMail(this.props.app.data.id, customerEmail, coCustomerEmail, 1, this.props.app.data.products);
        this.setState({confireModal: true});
    }

    render() {

        const customer = this.props.app;

        const IconAwesomePaperPlane = <svg xmlns="http://www.w3.org/2000/svg" width="17.982" height="18.273" viewBox="0 0 17.982 18.273">
