import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Row, Col, Button, Modal } from 'react-bootstrap';
import InputMask from 'react-input-mask';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import Footer from '../../components/Dealer/Footer';
import { IconArrowLeft, IconCaledar, IconStatusWaiting } from '../../assets/images';
import { product_options } from '../../assets/options';
import { requestFunding } from '../../redux/actions/admin';

class AppDetails extends Component {

    state = {
        deliveryDate: "",
        sentModal: false
    }

    componentDidMount() {
        document.addEventListener("mousedown", this.handleClickOutside);
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleModalClose = () => {
        this.setState({ sentModal: false });
    }

    handleRequestFundingClick = () => {
        this.props.requestFunding(this.props.app.data.id, this.state.deliveryDate);
        this.setState({ sentModal: true });
    }

    render() {
        const { app } = this.props;

        console.log(app.data)

        return (
            <div className="dealer dark-header">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        <span>APPLICATION DETAILS</span>
                    </HeaderCenter>
                    <HeaderRight>
                        <img src={IconStatusWaiting} />
                    </HeaderRight>
                </Header>
                <div className="application-main">

                    <div className="app-details">

                        <div className="contact-details">

                            <Row className="main-app">
                                {app.data.applicant && (
                                    <Col xs={12} md={6}>
                                        <label className="name">{app.data.applicant.name}</label>
                                        <div className="contact">{app.data.applicant.address} {app.data.applicant.city} {app.data.applicant.zip} {app.data.applicant.state}</div>
                                        <div className="contact">{app.data.applicant.phone}</div>
                                    </Col>
                                )}
                                {app.data.co_applicant && (
                                    <Col xs={12} md={6} className="border-left">
                                        <label className="name">{app.data.applicant.name}</label>
                                        <div className="contact">Address (static)</div>
                                        <div className="contact">{app.data.applicant.phone}</div>
                                    </Col>
                                )}
                            </Row>

                            <Row className="co-app">
                                <Col className="details">
                                    <label>FSP+F/M &nbsp;&nbsp;<span>TIER</span></label>
                                </Col>
                                <Col className="emp-name">
                                    <label>Assigned Employee</label>
                                </Col>
                                <Col className="date">
                                    <label>MM/DD/YY</label>
                                </Col>
                            </Row>
                        </div>                      

                        <div className="order-summary-header">ORDER SUMMARY</div>

                        <div className="order-summary-details">
                            <ul>
                                <li>1. Credit Application</li>
                                { app.data.products && app.data.products.map((product, id) => (
                                    <li key={id} >{id+2}. {product_options[product.type]} - ${product.balance} <br/> <span>{product.period} months</span></li>
                                )) }                                
                            </ul>
                            <div className="note">Applicant and co-applicant will each complete their own section of each form.</div>
                        </div>

                        <div className="order-summary-header">DOCUMENTS</div>

                        <div className="document-link"><a href="#">Product Order Documents Set</a></div>

                        <div className="bottom-footer">
                            <span>Delivery date: </span>
                            <label>
                                <InputMask
                                    className="form-control date-picker"
                                    pattern="[0-9]{2}/[0-9]{2}/[0-9]{4}"
                                    mask="99/99/9999"
                                    maskChar=""
                                    placeholder="MM/DD/YYYY"
                                    value={this.state.deliveryDate}
                                    // onChange={e => this.setState({ deliveryDate: e.target.value })}
                                />
                                <img src={IconCaledar} alt="" />
                            </label>
                            <Button>REQUEST FUNDING</Button>
                        </div>



                        {/* <div className="contact-details">
                            <div className="main-app">
                                <div><strong>Name Surname</strong></div>
                                <div>Address: 10/29 Scheme no indore 465441 (MP)</div>
                                <div>Phone: 7000905428</div>
                            </div>
                            { this.props.app.data.co_applicant && (
                                <div className="co-app">
                                    <div><strong>Applicantion Name</strong></div>
                                    <div>Address: 10/29 Scheme no indore 465441 (MP)</div>
                                    <div>Phone: 7000905428</div>
                                </div>
                            ) }
                        </div>
                        <div className="tab1">ORDER SUMMARY</div>
                        <div className="slips">
                            <div>1. Credit Application</div>
                            { this.props.app.data.products.map((product, id) => (
                                <div key={id} className="mt-20">
                                    <div>{id+2}. {product_options[product.type]} - ${product.balance}</div>
                                    <div>{product.period} months</div>
                                </div>
                            )) }
                            <div className="mt-20">
                                <div>{id+2}. {product_options[product.type]} - ${product.balance}</div>
                                <div>{product.period} months</div>
                            </div>
                            <div className="mt-20">Applicant and co-applicant will each complete their own section of each form.</div>
                        </div>
                        <div className="tab1">ORDER DOCUMENTS</div>
                        <div className="mt-20">
                            <div>Product Order Documents Set</div>
                        </div> */}



                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    app: state.sales.selectedApp
});

export default connect(
    mapStateToProps,
    {
        requestFunding
    }
)(AppDetails);
