import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft } from '../../../assets/images';
import { updateDealer } from '../../../redux/actions/admin';

function AddDealer(props) {

    const {
        history,
        selectedDealer
    } = props;

    console.log(selectedDealer)

    const [dealer, setDealer] = useState({
        id: null,
        company_name: null,
        email: null,
        contact_email: null,
        phone: null,
        street: null,
        city: null,
        state: null,
        zip: null,
    });

    const handleArrowBack = () => {       
        history.push('/admin/dealers')
    }

    const handleAddUpdateDealer = () => {    

        // const data = {
        //     id: this.state.id,
        //     company_name: this.state.company_name,
        //     email: this.state.email,
        //     contact_email: this.state.contact_email,
        //     phone: this.state.phone,
        //     street: this.state.street,
        //     city: this.state.city,
        //     state: this.state.state,
        //     zip: this.state.zip,
        // }

        // this.props.updateDealer(data)
        console.log(dealer)
    }

    return (
        <div className="dealer">

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt=""/>
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt=""/>
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <div className="container">                    
                <div className="styled-form">
                    <Form>
                        <Form.Group className="mb-18">
                            <Form.Label>Dealer Name</Form.Label>
                            <Form.Control value={dealer.company_name} placeholder="Dealer Name" onChange={(e) => setDealer({...dealer, company_name: e.target.value })}></Form.Control>
                        </Form.Group>
                        <div className="box">
                            <Form.Group className="mb-18">
                                <Form.Label>Main Dealer User Email</Form.Label>
                                <Form.Control value={dealer.email} placeholder="Main Dealer User Email" onChange={(e) => setDealer({...dealer, email: e.target.value })}></Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Form.Label>Dealer Contact Email</Form.Label>
                                <Form.Control value={dealer.contact_email} placeholder="Dealer Contact Email" onChange={(e) => setDealer({...dealer, contact_email: e.target.value })}></Form.Control>
                            </Form.Group>
                        </div>
                        <Form.Group className="mb-18">
                            <Form.Label>Phone</Form.Label>
                            <Form.Control value={dealer.phone} placeholder="Phone" onChange={(e) => setDealer({...dealer, phone: e.target.value })}></Form.Control>
                        </Form.Group>
                        <span className="divider"></span>
                        <span className="title">ADDRESS</span>
                        <Form.Group className="mb-18">
                            <Form.Label>Street</Form.Label>
                            <Form.Control value={dealer.street} placeholder="Street" onChange={(e) => setDealer({...dealer, street: e.target.value })}></Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>City</Form.Label>
                            <Form.Control value={dealer.city} placeholder="City" onChange={(e) => setDealer({...dealer, city: e.target.value })}></Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>State</Form.Label>
                            <select value={dealer.state} placeholder="State" onChange={(e) => setDealer({...dealer, state: e.target.value })}>
                                <option value="" disabled selected>State</option>
                                <option value="dummy_test">dummy_test</option>
                                <option value="dummy_test_1">dummy_test_1</option>
                                <option value="dummy_test_2">dummy_test_2</option>
                            </select>
                            <span className="select-dropdown">
                                <img className="main-logo" src={IconDownArrow} alt=""/>
                            </span>
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Form.Label>Zip Code</Form.Label>
                            <Form.Control value={dealer.zip} placeholder="Zip Code" onChange={(e) => setDealer({...dealer, zip: e.target.value })}></Form.Control>
                        </Form.Group>
                        <button type="submit" style={{"display": "none"}} />
                    </Form>
                </div>
            </div>

            <div className="footer-container"> 
                <button className="secondary" onClick={() => handleArrowBack()}>Cancel</button>
                <button className="secondary" onClick={() => handleAddUpdateDealer()}>{ dealer.id ? 'Update' : 'Add'}</button>
            </div>

        </div>
    )
}

const mapStateToProps = state => ({
    dealer: state.admin.selectedDealer
});

const mapDispatchToProps = dispatch => ({
    updateDealer: () => dispatch(updateDealer())
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddDealer);
