import React, { useState } from 'react';
import { connect } from 'react-redux';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { getMe } from '../../redux/actions/auth';
import { getUserDetails } from '../../redux/actions/userAction';
import { clearCustomer, changeCustomer } from '../../redux/actions/customer';

import { Form } from 'react-bootstrap';
import Input from '../../components/commons/input';
import Dropdown from '../../components/commons/dropdown';
import Loader from 'shared/Loader';

import { TCPLogo, IconArrowLeft } from '../../assets/images';

function HomeScreen(props) {



    const {
        history,
        dealer,
        addDealer,
        updateDealer,
        actionLoading,
    } = props;

    const [validationResult, setValidationResult] = useState(null);

    // componentDidMount = () => {
    //     this.props.getMe();
    //     this.props.getUserDetails()
    // }

    // onNewCustomer = () => {
    //     this.props.clearCustomer();
    //     this.props.changeCustomer({ order_type: 1 });
    //     this.props.history.push('/contact');
    // }

    // onReorderCustomer = () => {
    //     this.props.clearCustomer();
    //     this.props.changeCustomer({ order_type: 2 });
    //     this.props.history.push('/reorder');
    // }

    // onPrequalify = () => {
    //     this.props.history.push('/prequalify');
    // }

    // handleHomeClick = () => {
    //     this.props.history.replace('/');
    // }

    return (
        <div className="sales">

            { actionLoading && <Loader />}

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt="" />
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt="" />
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <div className='main-box'>
                <p className="title">ENTER CUSTOMER INFORMATION</p>
                <p className="sub-title">Short instruction donec in semper arcu. Sed quis ante cursus, porta lectus sit amet, mollis lacus.</p>
            </div>
            <div className="box">
                <Form.Group className="mb-18">
                    <Input
                        name={dealer.id ? "email" : "dealer_email"}
                        type="email"
                        regex="^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                        defaultValue={dealer.email}
                        label="Main Dealer User Email"
                        defaultText="Main Dealer User Email"
                        required={true}
                        error={{
                            'invalid': "Please enter valid Email address",
                            'empty': "Please enter Main Dealer User Email"
                        }}
                        validationResult={validationResult}
                    />
                </Form.Group>
                <Form.Group className="mb-18">
                    <Input
                        name={dealer.id ? "contact_email" : "dealer_contact_email"}
                        type="email"
                        regex="^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                        defaultValue={dealer.contact_email}
                        label="Dealer Contact Email"
                        defaultText="Dealer Contact Email"
                        required={true}
                        error={{
                            'invalid': "Please enter valid Email address",
                            'empty': "Please enter Dealer Contact Email"
                        }}
                        validationResult={validationResult}
                    />
                </Form.Group>
            </div>


            <div className="home">
                <div className="button" onClick={this.onNewCustomer}>
                    <div className="icon">
                        <img className="icon-new-customer" src={IconCustomerNew} alt="new" />
                    </div>
                    <div className="label">NEW CUSTOMER</div>
                </div>
                <div className="button button-reorder" onClick={this.onReorderCustomer}>
                    <div className="icon">
                        <img className="icon-reorder-customer" src={IconCustomerReorder} alt="reorder" />
                    </div>
                    <div className="label">REORDER CUSTOMER</div>
                </div>
                {/* <div className="button button-reorder" onClick={this.onPrequalify}>
                        <div className="icon">
                            <img className="icon-prequalify" src={IconCustomerPre} alt="pre" />
                        </div>
                        <div className="label">PREQUALIFY</div>
                    </div> */}
                <div className="button button-reorder">
                    <div className="icon">
                        <img className="icon-prequalify" src={IconCustomerHistory} alt="history" />
                    </div>
                    <div className="label">HISTORY</div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    avatar: state.auth.avatar
});

export default connect(
    mapStateToProps,
    {
        getMe,
        changeCustomer,
        clearCustomer,
        getUserDetails,
    }
)(HomeScreen);
