import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft, IconPreapprovalWaiting, IconPreapprovalDeclined, IconStatusConfirmed } from '../../assets/images';

import { getFundingRequests, setSelectedFundingRequest } from '../../redux/actions/admin';

class FundingRequests extends Component {

    componentDidMount() {
        this.props.getFundingRequests();
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleRowClick = (item) => {
        this.props.setSelectedFundingRequest(item);
        this.props.history.push(`/admin/funding/${item.id}`);
    }

    render() {
        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        <span>FUNDING REQUESTS</span>
                    </HeaderCenter>
                    <HeaderRight>

                    </HeaderRight>
                </Header>
                <div className="main">
                    <div className="sales-list">
                        { this.props.data.data.map(item => (
                            <Row key={item.id} onClick={() => this.handleRowClick(item)}>
                                <Col xs={4}>{ item.applicant.name }</Col>
                                <Col xs={4}>
                                    { item.products.map(product => (
                                        <div key={product.id}>{ product.type }, ${product.balance}</div>
                                    )) }
                                </Col>
                                <Col xs={4}>
                                    { item.status === 0 && <img src={IconPreapprovalWaiting} /> }
                                    { item.status === 1 && <img src={IconStatusConfirmed} /> }
                                    { item.status === 2 && <img src={IconPreapprovalDeclined} /> }
                                </Col>
                            </Row>
                        ))}
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    data: state.admin.fundings
});

export default connect(
    mapStateToProps,
    {
        getFundingRequests,
        setSelectedFundingRequest
    }
)(FundingRequests);
