import API from '../../api';

export const SALES_LIST = "SALES_LIST";
export const GET_APP_DETAIL = "GET_APP_DETAIL";
export const GET_APP_DETAIL_SUCCESS = "GET_APP_DETAIL_SUCCESS";
export const GET_APP_DETAIL_ERROR = "GET_APP_DETAIL_ERROR";
export const RESEND_APPLICATIONMAIL = "RESEND_APPLICATIONMAIL";

export function getSalesList() {
    return async function(dispatch) {
        try {
            const response = await API.get('/sales/apps');
            dispatch({
                type: SALES_LIST,
                payload: response.data
            });
        } catch(error) {
            console.log('error: ', error.response);
        }
    }
}

export function getAppDetailById(appId) {
    return async function(dispatch) {
        dispatch({
            type: GET_APP_DETAIL
        });
        try {
            const response = await API.get(`/sales/app/${appId}`)
            dispatch({
                type: GET_APP_DETAIL_SUCCESS,
                payload: response.data
            });
        } catch (error) {
            dispatch({
                type: GET_APP_DETAIL_ERROR
            });
        }
    }
}

export function resendApplicationMail(app_id, applicant_email, co_applicant_email, order_type, products ) {
    return async function(dispatch) {
        await API.put(`/sales/resendemail`, { app_id, applicant_email, co_applicant_email, order_type, products });
    }
}
