import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Form, Row, Col, Button, Modal } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft, IconCustomerEdit, iconNotify, IconSmallArrowRight, iconPaid } from '../../assets/images';
import { changeCustomer } from '../../redux/actions/customer';
import { getCustomerById, sendPreapproval } from '../../redux/actions/customers';
import CustomerLoanHistoryList from './CustomerLoanHistoryList';

class CustomerDetail extends Component {

    customerDetailsRef = React.createRef();

    state = {
        modalShow: false,
        commentBoxShow: false,
        currentWidth: 500,
        openLoadDetails: false
    }

    async componentDidMount() {
        const id = this.props.match.params.id;
        this.props.getCustomerById(id);
        if (this.customerDetailsRef !== undefined && this.customerDetailsRef.current.offsetWidth !== undefined) {
            this.setState({ currentWidth: this.customerDetailsRef.current.offsetWidth })
        }
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handlePreApprovalClick = async () => {
        const result = await this.props.sendPreapproval(this.props.customer.data.id);
        if (result) {
            this.setState({ modalShow: true });
        }
    }

    handleCommentClick = () => {
        this.setState({ commentBoxShow: !this.state.commentBoxShow })
    }

    handleModalClose = () => {
        this.setState({ modalShow: false });
    }

    handleOpenLoadDetails = () => {
        this.setState({ openLoadDetails: !this.state.openLoadDetails });
    }

    handleGenerateOrderClick = () => {
        const customer = this.props.customer.data;
        this.props.changeCustomer({
            order_type: 2,
            main_app: {
                name: customer.name,
                email: customer.email,
                dobY: customer.dobY,
                dobM: customer.dobM,
                dobD: customer.dobD,
                ssn: customer.ssn,
                dl: customer.dl,
                nod: customer.nod,
                cell_phone: customer.cell_phone,
                home_phone: customer.home_phone,
                street: customer.street,
                city: customer.city,
                state: customer.state,
                zip: customer.zip,
                yt1: "",
                own_or_rent: "",
                present_employer: "",
                yt2: "",
                job_title: "",
                employer_phone: "",
                monthly_income: "",
                additional_income: "",
                source: "",
                landlord_holder: "",
                monthly_rent_payment: ""
            },
        });
        this.props.history.push('/contact');
    }

    render() {
        const { loading, data } = this.props.customer;

        const historydata = [
            {},{}
        ]

        console.log(this.state.currentWidth);

        return (
            <div ref={this.customerDetailsRef} className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        {/* { loading && <span>Loading...</span> }
                        { !loading && Object.keys(data).length > 0 && <span>{data.name}</span> } */}
                        <span className="name">Sudeep Jain</span>
                    </HeaderCenter>
                    <HeaderRight>
                        <img src={IconCustomerEdit} />
                    </HeaderRight>
                </Header>

                {/* Demo Purpose */}
                <div className="main">
                    <div className="customer-details">

                        { this.state.currentWidth < 571 ? (
                            <>
                                <div className="address-and-buttons">
                                    <div className="address">
                                        <div>10/29 Scheme no</div>
                                        <div>452001 Indore</div>
                                        <div>MP</div>
                                    </div>
                                    {this.props.match.params.id !== 2 && (
                                        <div className="buttons">
                                            <div className="btn-custom button-preapproval" onClick={this.handlePreApprovalClick}>REQUEST PREAPPROVAL</div>
                                            <div className="btn-custom button-order" onClick={this.handleGenerateOrderClick}>GENERATE ORDER</div>
                                        </div>
                                    )}
                                </div>

                                { !this.state.commentBoxShow && (
                                    <>
                                        <div className="tab1">Contact</div>
                                        <div className="contact-info">
                                            <div className="details">
                                                <strong>TCP Customer Since: </strong>
                                                <span>02-09-2020</span>
                                            </div>
                                            <div className="details">
                                                <strong>Email: </strong>
                                                <span>Su.jain@example.com</span>
                                            </div>
                                            <div className="details">
                                                <strong>Date Of Birth: </strong>
                                                <span>03-06-1994</span>
                                            </div>
                                            <div className="details">
                                                <strong>TCP Customer Number: </strong>
                                                <span>34543563</span>
                                            </div>
                                            <div className="button-comment" onClick={this.handleCommentClick}>
                                                <div id="button">COMMENT</div>
                                            </div>
                                        </div>
                                    </>
                                ) }
                            </>

                        ) : (

                            <Row className="web-info-box">
                                <Col xs="6" className="text-left right-border">
                                    <div className="web-address-and-buttons">
                                        <div className="address">
                                            <div>10/29 Scheme no</div>
                                            <div>452001 Indore</div>
                                            <div>MP</div>
                                            <div className="buttons">
                                                <div className="btn-custom button-preapproval" onClick={this.handlePreApprovalClick}>REQUEST PREAPPROVAL</div>
                                                <div className="btn-custom button-order" onClick={this.handleGenerateOrderClick}>GENERATE ORDER</div>
                                            </div>
                                        </div>
                                    </div>
                                </Col>
                                <Col xs="6" className="text-left">
                                    <div className="web-address-and-buttons">
                                        <div className="address ml-50">
                                            <div>TCP Customer Since: 03/12/18</div>
                                            <div>Phone: 712-392-8874</div>
                                            <div>Email: ruby@email.com</div>
                                            <div>[Name of Dealer] Customer Number: 983</div>
                                            <div>TCP Customer Number: 103</div>
                                            {this.props.match.params.id !== 11112 && (
                                                <div className="buttons">
                                                    <div className="btn-custom button-preapproval" onClick={this.handleCommentClick}>COMMENT</div>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </Col>
                            </Row>
                        )}

                    </div>
                </div>

                <CustomerLoanHistoryList 
                    commentBoxShow = {this.state.commentBoxShow}
                    handleCommentClick = {this.handleCommentClick}
                />

                {/* Demo Purpose */}


                {/* { !loading && Object.keys(data).length > 0 && (
                    <div className="main">
                        <div className="customer-details">
                            <div className="address-and-buttons">
                                <div id="address">
                                    <div>{data.address}</div>
                                    <div>{data.zip} {data.city}</div>
                                    <div>{data.state}</div>
                                </div>
                                <div id="buttons">
                                    <div className="button-preapproval" onClick={this.handlePreApprovalClick}>REQUEST PREAPPROVAL</div>
                                    <div className="button-order" onClick={this.handleGenerateOrderClick}>GENERATE ORDER</div>
                                </div>
                            </div>

                            <div className="tab1">Contact</div>
                            <div className="contact-info">
                                <div>
                                    <strong>TCP Customer Since: </strong>
                                    <span>{data.created_at}</span>
                                </div>
                                { data.email && (
