import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Form } from 'react-bootstrap';
import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft } from '../../../assets/images';
import Input from '../../../components/commons/input';
import Dropdown from '../../../components/commons/dropdown';
import Loader from 'shared/Loader';

import { getFromData } from '../../../components/commons/utility';
import { updateStaff, addStaff } from '../../../redux/actions/dealer';

function AddStaff(props) {

    const {
        history,
        staff,
        addStaff,
        updateStaff,
        actionLoading,
        dropdownList = [
            {
                label: 'state 1',
                value: 'state 1'
            },
            {
                label: 'state 2',
                value: 'state 2'
            },
            {
                label: 'state 3',
                value: 'state 3'
            },
            {
                label: 'dummy_test',
                value: 'dummy_test'
            }
        ]
    } = props;

    const [validationResult, setValidationResult] = useState(null);

    const handleArrowBack = () => {
        history.push('/dealer/staff')
    }

    const handleSubmit = evt => {

        evt.preventDefault();
        const formData = getFromData(evt);

        setValidationResult(formData.validationResult);

        if(!formData.validationResult) {

            let data = formData.formData

            if(staff.id) {

                data = {...data, updated_email: data.email, updated_phone: data.phone}
                updateStaff(history, data)
            }
            else
            {
                addStaff(history, data)
            }
        }
    }

    return (
        <div className="dealer">

            { actionLoading && <Loader /> }

            <Header>
                <HeaderLeft>
                    <img src={IconArrowLeft} onClick={() => handleArrowBack()} alt="" />
                </HeaderLeft>
                <HeaderCenter>
                    <div className="header-main">
                        <img className="main-logo" src={TCPLogo} alt="" />
                    </div>
                </HeaderCenter>
                <HeaderRight></HeaderRight>
            </Header>

            <form onSubmit={(e) => handleSubmit(e)} noValidate>
                {staff.id && 
                    <Input 
                        name="id" 
                        type="hidden"
                        value={staff.id} 
                    />
                }
                <div className="container">
                    <div className="styled-form">
                        <Form.Group className="mb-18">
                            <Input 
                                name={ staff.id ? "company_name" : "staff_company_name" } 
                                type="text" 
                                defaultValue={staff.company_name} 
                                label="First Name"
                                defaultText = "First Name"
                                required = {true}
                                error = {{
                                    'empty': "Please enter First Name"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ staff.id ? "company_name" : "staff_company_name" } 
                                type="text" 
                                defaultValue={staff.company_name} 
                                label="Last Name"
                                defaultText = "Last Name"
                                required = {true}
                                error = {{
                                    'empty': "Please enter Last Name"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                        <div className="box">
                            <Form.Group className="mb-18">
                                <Input 
                                    name={ staff.id ? "email" : "staff_email" }
                                    type="email"
                                    regex = "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    defaultValue={staff.email} 
                                    label="Main Staff User Email"
                                    defaultText = "Main Staff User Email"
                                    required = {true}
                                    error = {{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Main Staff User Email"
                                    }}
                                    validationResult = {validationResult}
                                />
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Input 
                                    name={ staff.id ? "contact_email" : "staff_contact_email" }
                                    type="email"
                                    regex = "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                                    defaultValue={staff.contact_email} 
                                    label="Staff Contact Email"
                                    defaultText = "Staff Contact Email"
                                    required = {true}
                                    error = {{
                                        'invalid': "Please enter valid Email address",
                                        'empty': "Please enter Staff Contact Email"
                                    }}
                                    validationResult = {validationResult}
                                />
                            </Form.Group>
                        </div>
                        <Form.Group className="mb-18">
                            <Input 
                                name={ staff.id ? "phone" : "staff_phone" }
                                type="text"
                                defaultValue={staff.phone} 
                                label="Phone"
                                defaultText = "999-999-9999"
                                regex="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                                required = {true}
                                error = {{
                                    'invalid': "Please enter 10 digit number",
                                    'empty': "Please enter Phone number"
                                }}
                                validationResult = {validationResult}
                            />
                        </Form.Group>
                    </div>
                </div>
                <div className="footer-container">
                    <button className="secondary" onClick={() => handleArrowBack()}>Cancel</button>
                    <button className="secondary" type="submit">{staff.id ? 'Update' : 'Add'}</button>
                </div>
            </form>

        </div>
    )
}

const mapStateToProps = state => ({
    staff: state.dealer.selectedStaff,
    actionLoading: state.dealer.actionLoading
});

const mapDispatchToProps = dispatch => ({
    addStaff: (history, data) => dispatch(addStaff(history, data)),
    updateStaff: (history, data) => dispatch(updateStaff(history, data))
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AddStaff);
