import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Form, Row, Col } from 'react-bootstrap';

import { forgotPassword } from '../../redux/actions/auth';

import AuthContainer from '../../components/AuthContainer';

class ForgotPassword extends Component {
    state = {
        email: '',

        validated: false,
        error: ''
    };


    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({ validated: true });

        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.stopPropagation();
        } else {
            this.onConfirm();
        }
    }

    onConfirm = async () => {
        const { email } = this.state;
        const result = await this.props.forgotPassword({
            email
        });

        if (result.ok) {
            this.props.history.push({
                pathname: '/2fa',
                state: {
                    ...result.data,
                    screen: 'forgot'
                }
            })
        } else {
            this.setState({ error: result.error });
        }
    }

    render() {
        const { email, validated,  error } = this.state;
        return (
            <AuthContainer title="FORGOT PASSWORD">
                <Form noValidate validated={validated} onSubmit={this.handleSubmit} className="wrapper forgot-page">
                    <div className="inputs">
                        <Form.Group>
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                required
                                pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
                                className="input input-email"
                                value={email}
                                onChange={e => this.setState({ email: e.target.value }) }
                                isInvalid={this.state.invalidEmail}
                            />
                        </Form.Group>

                        <div className="error">
                            { error }
                        </div>
                    </div>
                    <div>
                        <Button className="button-action" type="submit">CONFIRM</Button>
                    </div>
                    <div className="auth-actions text-center">
                        <p>Back to &nbsp;
                        <span className="cursor-pointer goBack" onClick={() => this.props.history.replace('login')}>
                            Sign In
                        </span></p>
                    </div>                    
                </Form>
            </AuthContainer>
        )
    }
}

const mapStateToProps = state => ({

});

export default connect(
    mapStateToProps,
    {
        forgotPassword
    }
)(ForgotPassword);
