import {
    SEARCH_CUSTOMERS,
    SEARCH_CUSTOMERS_SUCCESS,
    SEARCH_CUSTOMERS_FAILED,
    GET_CUSTOMER,
    GET_CUSTOMER_SUCCESS,
    GET_CUSTOMER_FAILED
} from '../actions/customers';

const INIT_STATE = {
    loading: false,
    data: [],

    selected: {
        loading: false,
        data: {}
    }
};

export default function(state = INIT_STATE, action){
    switch (action.type) {
        case SEARCH_CUSTOMERS:
            return {
                ...state,
                loading: true,
                data: []
            }
        case SEARCH_CUSTOMERS_SUCCESS:
            return {
                ...state,
                loading: false,
                data: action.payload
            }
        case SEARCH_CUSTOMERS_FAILED:
            return {
                ...state,
                loading: false,
                data: []                
            }
        case GET_CUSTOMER:
            return {
                ...state,
                selected: {
                    loading: true,
                    data: {}
                }
            }
        case GET_CUSTOMER_SUCCESS:
            return {
                ...state,
                selected: {
                    loading: false,
                    data: action.payload
                }
            }
        case GET_CUSTOMER_FAILED:
            return {
                ...state,
                selected: {
                    loading: false,
                    data: {}
                }
            }
        default:
            return state;
    }
}
