import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight, HeaderLogo } from '../../components/Dealer/Header';
import { IconDashboardSales, IconDashboardStaff, IconDashboardSalesApp, IconDashboardPending, IconDashboardApproved, IconDashboardDeclined, IconDashboardFunded, IconDashboardCanceled, IconDashboardPreApprovals, IconDashboardIncomplete, IconSearchInActive } from '../../assets/images';

import { searchCustomers } from '../../redux/actions/customers';

class HomeScreen extends Component {

    state = {
        lastName: '',
        city: '',

        searchShow: false
    }

    onSalesApp = () => {
        this.props.history.push('/sales');
    }

    onSales = () => {
        this.props.history.push('/sales-list');
    }

    handleSearchClick = () => {
        // const body = {
        //     last_name: this.state.lastName,
        //     city: this.state.city
        // }
        // this.props.searchCustomers(body);
        this.setState({ searchShow: true });
    }

    handleSearchRowClick = (cifno) => {
        this.props.history.push(`/customer/${cifno}`);
    }

    render() {
        const { lastName, city, searchShow } = this.state;
        // const { customers } = this.props;

        const customers = [
            {

            },
            {

            },
            {

            }
        ]

        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft />
                    <HeaderCenter>
                        <HeaderLogo />
                    </HeaderCenter>
                    <HeaderRight />
                </Header>
                <div className="main">
                    <div className="dashboard">
                        <div className="customer-search">
                            <div id="header">
                                <span>CUSTOMER SEARCH</span>
                                <div id="icon">
                                    <img src={IconSearchInActive} onClick={this.handleSearchClick} />
                                </div>
                            </div>
                            <div id="form">
                                <Form.Group>
                                    <Form.Label>Customer Last Name</Form.Label>
                                    <Form.Control value={lastName} onChange={(e) => this.setState({ lastName: e.target.value })}></Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Label>City</Form.Label>
                                    <Form.Control value={city} onChange={(e) => this.setState({ city: e.target.value })}></Form.Control>
                                </Form.Group>
                            </div>
                        </div>

                        { !searchShow && (
                            <div>
                                <div className="sales-details">
                                    <div id="box">
                                        <div className="part1">
                                            <div id="pre-approvals">
                                                <img src={IconDashboardPreApprovals} />
                                                <div>
                                                    <div>PRE-APPROVALS</div>
                                                    <div className="count">05</div>
                                                </div>
                                            </div>
                                            <div id="incomplete">
                                                <img src={IconDashboardIncomplete} />
                                                <div>
                                                    <div>INCOMPLETE</div>
                                                    <div className="count">03</div>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="part2">
                                            <div>
                                                <div className="count">87</div>
                                                <img src={IconDashboardPending} />
                                                <div className="status">Pending</div>
                                            </div>
                                            <div>
                                                <div className="count">12</div>
                                                <img src={IconDashboardDeclined} />
                                                <div className="status">Declined</div>
                                            </div>
                                            <div>
                                                <div className="count">56</div>
                                                <img src={IconDashboardApproved} />
                                                <div className="status">Approved</div>
                                            </div>
                                            <div>
                                                <div className="count">44</div>
                                                    <img src={IconDashboardFunded} />
                                                <div className="status">Funded</div>
                                            </div>
                                            <div>
                                                <div className="count">07</div>
                                                    <img src={IconDashboardCanceled} />
                                                <div className="status">Canceled</div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="action-button" onClick={this.onSales}>
                                        <div id="icon">
                                            <img src={IconDashboardSales} />
                                        </div>
                                        <div id="title">SALES</div>
                                    </div>
                                </div>

                                <div className="actions">
                                    <div className="action-button">
                                        <div id="icon">
                                            <img src={IconDashboardStaff} />
                                        </div>
                                        <div id="title">STAFF MANAGEMENT</div>
                                    </div>
                                    <div className="action-button" onClick={this.onSalesApp}>
                                        <div id="icon">
                                            <img src={IconDashboardSalesApp} />
                                        </div>
                                        <div id="title">SALES APP</div>
                                    </div>
                                </div>
                            </div>
                        )}

                        { searchShow && (
                            <div className="search-modal">
                                <div className="status-bar">
                                    <div id="status">
                                        {/* { customers.loading && <span>searching...</span> } */}
                                        { !customers.loading && <span className="result-count">{customers.length} matches found...</span> }
                                    </div>
                                    <div id="action">
                                        <div className="cancel-button" onClick={() => this.setState({ searchShow: false })}>
                                            CANCEL
                                        </div>
