import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft, IconPreapprovalWaiting, IconStatusConfirmed, IconDashboardDeclined, IconDashboardCanceled,IconDashboardFunded } from '../../assets/images';

import { setSelectedPendingApplication } from '../../redux/actions/admin';
import { getSalesList } from '../../redux/actions/sales';

class PendingApplications extends Component {

    componentDidMount() {
        this.props.getSalesList();
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleRowClick = (item) => {
        this.props.setSelectedPendingApplication(item);
        this.props.history.push(`/admin/pendingApplication/${item.id}`);
        // alert("API Pending");
    }

    render() {

        const apps = this.props.apps.filter(a => a.hello_sign && a.hello_sign.length > 0 && a.hello_sign[0].status_code === "signed");

        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        <span>PENDING APPLICATIONS</span>
                    </HeaderCenter>
                    <HeaderRight>

                    </HeaderRight>
                </Header>
                <div className="main">
                    <div className="sales-list">
                        { apps.map(item => (
                            <Row key={item.id} className="single-row" onClick={() => this.handleRowClick(item)}>
                                <Col xs={4} className="m-auto text-left">
                                    <b>{ item.name }</b>                                    
                                    <small>{ item.products.map(product => (
                                        <div key={product.id}>{ product.type }, ${product.balance}</div>
                                    )) }</small>
                                </Col>
                                <Col xs={4} className="m-auto">
                                    <div>{item.street}, {item.zip}</div>
                                    <div>{item.city}, {item.state}</div>
                                </Col>
                                <Col xs={4} className="m-auto text-right">
                                    { item.status === "waiting" && <img src={IconPreapprovalWaiting} width="39" height="39"/> }
                                    { item.status === "approved" && <img src={IconStatusConfirmed} width="39" height="39"/> }
                                    { item.status === "declined" && <img src={IconDashboardDeclined} width="39" height="39"/> }
                                    { item.status === "canceled" && <img src={IconDashboardCanceled} width="39" height="39"/> }
                                    { item.status === "funded" && <img src={IconDashboardFunded} width="39" height="39"/> }
                                </Col>
                            </Row>
                        ))}
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    apps: state.sales.apps
});

export default connect(
    mapStateToProps,
    {
        getSalesList,
        setSelectedPendingApplication
    }
)(PendingApplications);
