import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header from '../../components/Sales/Header';
import { IconDashboardSales, IconHome, IconDashboardStaff, IconDashboardSalesApp, iconNotify, IconDashboardPending, IconDashboardApproved, IconDashboardDeclined, IconDashboardFunded, IconDashboardCanceled, IconDashboardPreApprovals, IconDashboardIncomplete, IconSearchInActive, IconSmallArrowRight } from '../../assets/images';
import { getSalesList, getAppDetailById } from '../../redux/actions/sales';
import { searchCustomers } from '../../redux/actions/customers';

class HomeScreen extends Component {

    state = {
        lastName: '',
        city: '',

        searchShow: false
    }

    componentDidMount() {
        this.props.getSalesList();
    }

    onSalesApp = () => {
        this.props.history.push('/sales');
    }

    onSales = (status) => {
        this.props.history.push(`/sales-list`);
    }

    handleSearchClick = () => {
        const body = {
            last_name: this.state.lastName,
            city: this.state.city
        }
        this.props.searchCustomers(body);
        this.setState({ searchShow: true });
    }

    handleSearchRowClick = (cifno) => {
        this.props.history.push(`/customer/${cifno}`);
    }

    render() {
        const { lastName, city, searchShow } = this.state;
        // const { customers } = this.props;

        const customers = [
            {

            },
            {

            },
            {

            }
        ]

        let apps = this.props.apps;

        console.log(apps);

        const preApprovalCount = apps.filter(a => (a.status === 'waiting' || a.status === 'approved' || a.status === 'declined')).length;
        const incompleteCount = apps.filter(a => ((a.status === 'waiting' || a.status === 'approved' || a.status === 'declined') && (a.hello_sign.status_code === "declined" || a.hello_sign.status_code === "error_unknown" || a.hello_sign.status_code === "error_file" || a.hello_sign.status_code === "error_component_position" || a.hello_sign.status_code === "error_text_tag" || a.hello_sign.last_viewed_at === null|| a.hello_sign.status_code === "awaiting_signature" ))).length;
        const pendingCount = 0;
        const declinedCount = 0;
        const approvedCount = apps.filter(a => (a.status === 'approved' && a.hello_sign.status_code === "signed")).length;
        const fundedCount = 0;
        const canceledCount = 0;


        return (
            <div className="dealer sales">
                <Header isHome={true} history={this.props.history} avatar={this.props.avatar} className="home-header">
                </Header>
                <div className="main">
                    <div className="dashboard">
                        <div className="customer-search">
                            <div id="header">
                                <span>CUSTOMER SEARCH</span>
                                <div id="icon">
                                    <img src={IconSearchInActive} onClick={this.handleSearchClick} />
