import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Row, Col, Button } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../components/Dealer/Header';
import { IconArrowLeft } from '../../assets/images';

import { changeSelectedPreApprovalStatus } from '../../redux/actions/admin';

class PreApprovalItem extends Component {

    componentDidMount() {
    }

    handleArrowBack = () => {
        this.props.history.goBack();
    }

    handleApproveClick = () => {
        this.props.changeSelectedPreApprovalStatus(this.props.item.id, 1);
        setTimeout(function(){ 
            this.props.history.push(`/admin/preapprovals`);
        }, 3000);
    }

    handleDeclineClick = () => {
        this.props.changeSelectedPreApprovalStatus(this.props.item.id, 2);
    }

    render() {
        const { item } = this.props;
        if (Object.keys(item).length === 0) {
            return (
                <div>

                </div>
            )
        }
        const { id, customer, created_at, status } = item;

        return (
            <div className="dealer">
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} />
                    </HeaderLeft>
                    <HeaderCenter>
                        <span>{customer.name}</span>
                    </HeaderCenter>
                    <HeaderRight>

                    </HeaderRight>
                </Header>
                <div className="main">
                    <div className="customer-details">
                        <div className="contact-info">
                            <div>
                                <strong>PreApproval Created At: </strong>
                                <span>{created_at}</span>
                            </div>
                            { customer.email && (
                                <div>
                                    <strong>Customer Email: </strong>
                                    <span>{customer.email}</span>
                                </div>
                            ) }
                            <div style={{ marginTop: 20, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                                { status === 0 && (
                                    <>
                                        <Button
                                            style={{ marginRight: 20 }}
                                            variant="success"
                                            onClick={this.handleApproveClick}
                                        >
                                            Approve
                                        </Button>
                                        <Button
                                            variant="danger"
                                            onClick={this.handleDeclineClick}
                                        >
                                            Decline
                                        </Button>
                                    </>
                                ) }

                                { status === 1 && (
                                    <div style={{ color: '#009e87', fontSize: 16, fontWeight: 'bold' }}>
                                        Preapproval accepted
                                    </div>
                                ) }

                                { status === 2 && (
                                    <div style={{ color: '#d81740', fontSize: 16, fontWeight: 'bold' }}>
                                        Preapproval declined
                                    </div>
                                ) }
                            </div>
                        </div>                        
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    item: state.admin.selectedPreapproval
});

export default connect(
    mapStateToProps,
    {
        changeSelectedPreApprovalStatus
    }
)(PreApprovalItem);
