import React, { Component } from 'react';
import { connect } from 'react-redux';

import { Form, Row, Col } from 'react-bootstrap';

import Header, { HeaderLeft, HeaderCenter, HeaderRight } from '../../../components/Dealer/Header';
import { TCPLogo, IconDownArrow, IconArrowLeft, IconDeleteNew, IconPreapprovalWaiting, IconPreapprovalDeclined, IconStatusConfirmed } from '../../../assets/images';

import { getDealersRequests, getFundingRequests, setSelectedFundingRequest } from '../../../redux/actions/admin';
import Loader from 'shared/Loader';

class AddDealer extends Component {

    state = {
        seachKeyword: null,
        dealer: null,
        openDealerIndex: null
    }

    componentDidMount() {
        this.props.getDealersRequests();
    }

    handleArrowBack = () => {       
        const { history } = this.props;
        history.push('/admin/dealers')
    }

    handleAddDealer = () => {       
        const { history } = this.props;
        this.props.history.push(`/admin/addDealer`);
    }

    render() {

        const { dealer, seachKeyword, openDealerIndex } = this.state;

        const id = parseInt(this.props.match.params.id);
        const { dealers } = this.props;

        if(dealer)
        {
            this.setState({
                dealer: dealers.data ? dealers.data.filter(d => d.id === id)[0] : null
            });    
        }

        console.log(dealer)


        return (
            <div className="dealer">
                {
                    dealers.loading && <Loader />
                }
                <Header>
                    <HeaderLeft>
                        <img src={IconArrowLeft} onClick={this.handleArrowBack} alt=""/>
                    </HeaderLeft>
                    <HeaderCenter>
                        <div className="header-main">
                            <img className="main-logo" src={TCPLogo} alt=""/>
                        </div>
                    </HeaderCenter>
                    <HeaderRight></HeaderRight>
                </Header>
                <div className="container">                    
                    <div className="styled-form">
                        <Form onSubmit={this.handleSearchClick}>
                            <Form.Group className="mb-18">
                                <Form.Label>Dealer Name</Form.Label>
                                <Form.Control value={''} placeholder="Dealer Name" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                            </Form.Group>
                            <div className="box">
                                <Form.Group className="mb-18">
                                    <Form.Label>Main Dealer User Email</Form.Label>
                                    <Form.Control value={seachKeyword} placeholder="Main Dealer User Email" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                                </Form.Group>
                                <Form.Group className="mb-18">
                                    <Form.Label>Dealer Contact Email</Form.Label>
                                    <Form.Control value={seachKeyword} placeholder="Dealer Contact Email" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                                </Form.Group>
                            </div>
                            <Form.Group className="mb-18">
                                <Form.Label>Phone</Form.Label>
                                <Form.Control value={seachKeyword} placeholder="Phone" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                            </Form.Group>
                            <span className="divider"></span>
                            <span className="title">ADDRESS</span>
                            <Form.Group className="mb-18">
                                <Form.Label>Street</Form.Label>
                                <Form.Control value={seachKeyword} placeholder="Street" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Form.Label>City</Form.Label>
                                <Form.Control value={seachKeyword} placeholder="City" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Form.Label>State</Form.Label>
                                <select value={''} placeholder="State">
                                    <option value="" disabled selected>State</option>
                                    <option value="grapefruit">Grapefruit</option>
                                    <option value="lime">Lime</option>
                                    <option value="coconut">Coconut</option>
                                    <option value="mango">Mango</option>
                                </select>
                                <span className="select-dropdown">
                                    <img className="main-logo" src={IconDownArrow} alt=""/>
                                </span>
                            </Form.Group>
                            <Form.Group className="mb-18">
                                <Form.Label>Zip Code</Form.Label>
                                <Form.Control value={seachKeyword} placeholder="Zip Code" onChange={(e) => this.setState({ seachKeyword: e.target.value })}></Form.Control>
                            </Form.Group>
                            <button type="submit" style={{"display": "none"}} />
                        </Form>
                    </div>
                </div>
                <div className="footer-container"> 
                    <button className="secondary" onClick={this.handleArrowBack}>Cancel</button>
                    <button className="secondary" onClick={this.handleArrowBack}>Update</button>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    dealers: state.admin.dealers
});

export default connect(
    mapStateToProps,
    {
        getFundingRequests,
        getDealersRequests,
        setSelectedFundingRequest
    }
)(AddDealer);
